/**
 * @file 折线图组件
 * @author liuyuxin06@baidu.com
 */
/* eslint-disable fecs-camelcase */
import {Component} from 'san';
import styles from './lineChart.module.less';
import * as echarts from 'echarts/lib/echarts';
import 'echarts/lib/chart/line';
import 'echarts/lib/component/title';
import 'echarts/lib/component/legend';
import 'echarts/lib/component/tooltip';
import 'echarts/lib/component/grid';

import ubc from '@baidu/ubc-report-sdk';
const nightLineColor = {
    '新增确诊': {
        color: ['#FF6A57'],
        color_night: ['#7F342B']
    },
    '确诊治愈死亡': {
        color: ['#E83132', '#10AEB5', '#4D5054'],
        color_night: ['#79251F', '#07565A', '#3C4045']
    },
    '死亡治愈': {
        color: ['#10AEB5', '#4D5054'],
        color_night: ['#07565A', '#3C4045']
    },
    '新增本土': {
        color: ['#FF6A57'],
        color_night: ['#7F342B']
    },
    '新增无症状': {
        color: ['#E86D48'],
        color_night: ['#E86D48']
    }
};
export default class LineChart extends Component {
    /* eslint-disable max-len */
    static template = `
        <div class="{{styles['lineChart']}}">
            <div class="{{styles['lineChart-title']}}">{{chartTitle}}</div>
            <div class="{{styles['lineChart-title-extra']}}">趋势图数据更新至前一日</div>
            <div style="position: relative;height: {{chartsHeight}}px; margin-top: .625rem; overflow: hidden;">
                <div s-for="item, index in feed.items"
                    class="{{styles['lineChart-items']}}"
                    style="{{tabChoosedIndex === index ? '' : 'visibility: hidden;'}}">
                    <div style="width: {{chartsWidth}}px;height: {{chartsHeight}}px;"
                        id="{{'lineChart-container-' + index}}"
                        class="{{styles['lineChart-container']}}"></div>
                </div>
            </div>
            <div s-if="{{notInForeign && tabChoosedIndex === 0}}"
                class="{{styles['lineChart-group']}}"
            >
                <div s-for="item,index in groupOption"
                    class="{{index === groupSelectIndex ?  styles['lineChart-group-active'] : styles['lineChart-group-unactive']}}"
                    on-click="groupClick(index)"
                >{{item}}</div>
            </div>
            <div s-if="{{buttons.length > 1}}"
                class="{{styles['lineChart-bottom']}}">
                <div s-for="button, index in buttons"
                    style="{{index < buttons.length - 1 ? 'margin-right: .25rem;' : ''}}"
                    class="{{tabChoosedIndex === index ?
                        styles['lineChart-bottom-active'] : styles['lineChart-bottom-button']}}"
                    on-click="buttonClick($event,index)">
                    <span>{{button[0]}}</span>
                    <span style="{{button[0] ? 'margin-top: .25rem;': ''}}">{{button[1]}}</span>
                </div>
            </div>
            <div s-else
                class="{{styles['lineChart-bottom-remark']}}">*趋势图每周更新一次</div>
        </div>
    `;
    /* eslint-enable max-len */
    static components = {
    };

    static computed = {
        feedItems() {
            return this.data.get('feed.items');
        },
        chartTitle() {
            const feed = this.data.get('feed');
            const province = this.data.get('province');
            const tabChoosedIndex = this.data.get('tabChoosedIndex');
            if (tabChoosedIndex > feed.items.length - 1 || feed.key === 'allForeignTrend') {
                return feed.items[0].title || '';
            }
            return `${province ? province : '本地'}` + feed.items[tabChoosedIndex].title;
        },
        buttons() {
            const feedItems = this.data.get('feedItems');
            return feedItems.map((item, index) => {
                return item.buttonName;
            });
        },
        notInForeign() {
            // 判断曲线图是否在国外疫情tab下，国外疫情tab的折线图不展示『按日、按月』
            const feed = this.data.get('feed');
            return feed.key && feed.key !== 'allForeignTrend';
        },
        chartsWidth() {
            return this.data.get('underTalosTab')
                ? document.documentElement.clientWidth - 26
                : document.documentElement.clientWidth - 52;
        }
    };

    attached() {
        const feedItems = this.data.get('feedItems');
        this.data.set('itemsLen', feedItems.length);
        this.drawEcharts(feedItems);
        this.watch('feed.items', newItems => {
            setTimeout(() => {
                const theme = this.data.get('theme');
                this.drawEcharts(newItems, theme);
                this.data.set('tabChoosedIndex', 0);
                this.data.set('groupSelectIndex', 0);
            }, 0);
        });
        this.watch('theme', val => {
            const feedItems = this.data.get('feedItems');
            this.drawEcharts(feedItems, val);
        });
        window.addEventListener('resize', this.resizeChat.bind(this));
    }
    groupClick(index) {
        const feedItems = this.data.get('feedItems');
        const theme = this.data.get('theme');
        const monthItems = this.data.get('feed.monthItems');
        this.data.set('groupSelectIndex', index);
        const nextDrawOption = index === 0 ? feedItems.slice(0, 2) : monthItems;
        this.drawEcharts(nextDrawOption, theme);
    }
    resizeChat() {
        let newChartsWidth = this.data.get('underTalosTab')
            ? document.documentElement.clientWidth - 26
            : document.documentElement.clientWidth - 52;
        if (this.data.get('chartsWidth') === newChartsWidth) {
            return;
        }
        if (this.resizeTimer) {
            clearTimeout(this.resizeTimer);
        }
        this.data.set('chartsWidth', newChartsWidth);
        this.resizeTimer = setTimeout(() => {
            this.resizeTimer = null;
            const chartsArr = this.data.get('chartsArr');
            chartsArr.forEach(chart => {
                chart && chart.resize();
            });
        }, 300);

    }
    // 重绘echarts
    drawEcharts(newItems, theme = '') {
        let chartsArr = [];
        for (let index = 0; index < newItems.length; index++) {
            let eachNewItems = newItems[index];
            let showTip = false;
            let myChart = echarts.getInstanceByDom(document.getElementById(`lineChart-container-${index}`));
            if (myChart == null) { // 如果不存在，就进行初始化。
                myChart = echarts.init(document.getElementById(`lineChart-container-${index}`));
            }
            chartsArr.push(myChart);
            myChart && myChart.clear();
            // 监听finished事件，echarts渲染完成后展示最近一天的焦点和浮层
            myChart && myChart.on('finished', () => {
                if (!showTip) {
                    this.data.get('chartsArr')[index].dispatchAction({
                        type: 'showTip',
                        dataIndex: eachNewItems.xAxisData.length - 1, // 显示哪个横坐标
                        seriesIndex: 0,
                        position: ['50%', '30%']
                    });
                    showTip = true;
                    this.data.get('chartsArr')[index].off('finished');
                }
            });
            if (eachNewItems) {
                let {legendData, xAxisData, series} = eachNewItems;
                let option = this.getBaseOptions(legendData, xAxisData, series);
                option = this.changeEchartsStyle(option, theme);
                myChart.setOption(option);
            }
        }
        this.data.set('chartsArr', chartsArr);
    }
    // 改变曲线图样式
    changeEchartsStyle(option, setTheme = '') {
        let theme = setTheme ? setTheme : this.data.get('theme');
        if (theme === 'kangyi-night') {
            option.xAxis.axisLabel.color = '#444';
            option.yAxis.axisLabel.color = '#444';
            option.yAxis.splitLine.lineStyle.color = '#303030';
            option.title.textStyle.color = '#444';
            option.tooltip.backgroundColor = '#121212';
            option.tooltip.textStyle.color = '#666';
            option.emphasis.itemStyle.color = '#191919';
            let legendStr = option.legend.data.join('');
            option.color = nightLineColor[legendStr].color_night;
        } else if (theme === 'kangyi-dark') {
            option.xAxis.axisLabel.color = 'rgba(255,255,255,0.5)';
            option.yAxis.axisLabel.color = 'rgba(255,255,255,0.5)';
            option.yAxis.splitLine.lineStyle.color = '#222';
            option.title.textStyle.color = 'rgba(255,255,255,0.5)';
            option.tooltip.backgroundColor = '#1e1f24';
            option.tooltip.textStyle.color = 'rgba(255,255,255,0.8)';
            option.emphasis.itemStyle.color = '#080808';
            let legendStr = option.legend.data.join('');
            option.color = nightLineColor[legendStr].color;
        } else {
            option.xAxis.axisLabel.color = '#848691';
            option.yAxis.axisLabel.color = '#848691';
            option.yAxis.splitLine.lineStyle.color = '#F8F8F8';
            option.title.textStyle.color = '#848691';
            option.tooltip.backgroundColor = '#fff';
            option.tooltip.textStyle.color = '#333';
            option.emphasis.itemStyle.color = '#fff';
            let legendStr = option.legend.data.join('');
            option.color = nightLineColor[legendStr].color;
        }
        return option;
    }
    buttonClick(e, index) {
        e.event && e.event.stopPropagation();
        this.data.set('tabChoosedIndex', index);
        ubc('event', {
            page: 'local',
            type: 'clk',
            value: 'trend',
            ext: {
                pos: index
            }
        });
    }
    getBaseOptions(legend, xAxis, series) {
        // 设置x轴展示内容
        let interval = 0;
        // 小屏最多展示 14 天，大屏最多展示 20 天
        const size = document.body.offsetWidth >= 375 ? 20 : 14;
        let labelNum = series[0].data.length;
        let dataLength = xAxis.length;
        if (dataLength > size) {
            const multi = Math.ceil(dataLength / size);
            labelNum = Math.ceil(dataLength / multi);
            interval = multi - 1;
        }
        const baseOption = {
            animationDuration: 0,
            title: {
                text: '单位: 例',
                textStyle: {
                    color: '#848691',
                    fontSize: 12,
                    fontWeight: 'normal'
                },
                textAlign: 'left',
                right: '2'
            },
            tooltip: {
                show: true,
                trigger: 'axis',
                confine: true,
                backgroundColor: '#fff',
                axisPointer: {
                    lineStyle: {
                        type: 'dotted',
                        color: 'rgba(77, 80, 84, .4)'
                    }
                },
                // axisPointer: {
                //     type: 'none'
                // },
                borderWidth: 0,
                padding: [5, 8, 5, 8],
                borderColor: '#E83132',
                // extraCssText: 'text-align: left;box-shadow: 0 5px 15px 0 rgba(0, 0, 0, 0.1);border-radius: 5px;',
                textStyle: {
                    color: '#333333',
                    fontSize: 12,
                    lineHeight: 18
                },
                formatter(params) {
                    let html = [];
                    let lineNames = [''];
                    // 按seriesName的字数多少进行排序,从多到少
                    const sortedParams = params.sort((prev, next) => {
                        const len = lineNames.length;
                        return (len - lineNames.indexOf(next.seriesName)) - (len - lineNames.indexOf(prev.seriesName));
                    });

                    // 提示框中加入日期
                    html.push(
                        `<span style="margin-left: 5px;">${sortedParams[0].name}</span>`
                    );

                    for (let i = 0; i < sortedParams.length; i++) {
                        let item = sortedParams[i];
                        let value = item.value;
                        let str = '';
                        let valueIsEmpty = item.value === undefined || item.value === null || item.value === '';
                        if (!valueIsEmpty && !isNaN(item.value)) {
                            str = `<span style="display: inline-block;transform: scale(0.7);margin-left: -3px;">
                                    ${echarts.format.getTooltipMarker({color: item.color})}
                                </span>`
                                + `<span>${item.seriesName}: ${value}</span>`;
                        }
                        if (str !== '') {
                            html.push(str);
                        }
                    }
                    if (html.join('') === '') {
                        return;
                    } else {
                        return html.join('<br>');
                    }
                }
            },
            legend: {
                icon: 'circle',
                color: ['#10AEB5'],
                itemHeight: 6,
                itemWidth: 6,
                itemGap: 15,
                padding: [5, 9, 5, 9],
                borderRadius: 5,
                orient: 'horizontal',
                left: -7,
                textStyle: {
                    color: '#848691',
                    padding: [2, 0, 0, 0]
                },
                selectedMode: false, // 设置图例不可点击
                data: legend
            },
            xAxis: {
                type: 'category',
                boundaryGap: [0, 1],
                axisLabel: {
                    fontSize: 10,
                    color: '#848691',
                    interval(index, value) {
                        if (interval === 0) {
                            return true;
                        }
                        else {
                            return (dataLength - index) % (interval + 1) === 1;
                        }
                    }
                },
                axisTick: {
                    show: false,
                    inside: true
                },
                axisLine: {
                    show: false
                },
                silent: true,
                data: xAxis
            },
            yAxis: {
                type: 'value',
                nameTextStyle: {
                    align: 'left'
                },
                nameGap: 20,
                splitNumber: 5, // 坐标轴的分割段数，只是个预估值，会根据实际情况自动调整
                silent: true, // 坐标轴是否是静态无法交互
                axisTick: {
                    show: false,
                    inside: true
                },
                axisLine: {
                    show: false
                },
                axisLabel: {
                    fontSize: 10,
                    color: '#848691'
                },
                splitLine: {
                    lineStyle: {
                        color: '#F8F8F8'
                    }
                }
            },
            emphasis: {
                itemStyle: {
                    // 高亮时点的颜色。
                    borderWidth: 1,
                    color: '#fff'
                }
            },
            series,
            grid: {
                top: 31,
                left: 2,
                right: '1%',
                bottom: '3%',
                width: '98%',
                containLabel: true
            }
        };
        if (labelNum > 10) {
            baseOption.xAxis.axisLabel.rotate = 45;
        }
        return baseOption;
    }

    initData() {
        return {
            styles,
            feed: {},
            chartsHeight: 244,
            tabChoosedIndex: 0,
            myChartEle: [],
            groupOption: ['按日', '按月'],
            groupSelectIndex: 0
        };
    }
}
