/**
 * @file 最热排行
 * @author shiqinqin@baidu.com
 */

import {Component} from 'san';
import {env} from '@baidu/auc-base/env';
import ubc from '@baidu/ubc-report-sdk';
import invoke from '@baidu/xbox/na/invoke';
import s from './hotRank.module.less';

export default class HotRank extends Component {
    /* eslint-disable max-len */
    static template = `
        <div s-if="{{itemData.board_list && itemData.board_list.length > 0}}" 
            class="{{s['hotRank']}}" style="{{theme | getBgStyle(itemData)}}">
            <div class="{{s['rankHeader']}}" style="{{getComputedHeight}}{{theme | getHeaderBgStyle(itemData)}}">
                <div>
                    <img
                        s-if="itemData.board_icon"
                        class="{{s['rankHeader-icon']}}"
                        src="{{theme | getHeaderTitleIcon(itemData)}}" />
                    <span
                        class="{{s['rankHeader-title']}}"
                        style="{{theme | getHeaderTitleColorStyle(itemData)}}">{{itemData.board_name}}</span>
                </div>
                <div s-if="itemData.more_text" on-click="onMoreClick">
                    <span
                        class="{{s['rankHeader-more-title']}}"
                        style="{{theme | getHeaderMoreColorStyle(itemData)}}">{{itemData.more_text}}</span>
                    <i s-if="itemData.more_icon"
                        class="{{s['rankHeader-more-icon']}}"
                        style="{{theme | getHeaderMoreIcon(itemData)}}"></i>
                </div>
            </div>
            <div class="{{s['hotRank-list']}}">
                <div
                    class="{{s['hotRank-list-item']}}"
                    style="{{theme | getBorderColorStyle(item)}};"
                    s-for="item, index in boardList"
                    on-click="itemClick($event, item)">
                    <div class="{{s['hotRank-item-info']}}">
                        <div class="{{s['hotRank-item-avatar-box']}}" style="{{borderStyle}}">
                            <img src="{{placeHolder}}" class="{{s['hotRank-item-holder']}}"/>
                            <img s-if="item.data.image" class="{{s['hotRank-item-avatar']}}" src="{{item.data.image}}" />
                            <div
                                s-if="item.data.index"
                                class="{{s['hotRank-item-rank']}}"
                                style="{{theme | getRankBgStyle(item)}}
                                {{theme | getRankColorStyle(item)}}">{{item.data.index}}</div>
                        </div>
                        <div class="{{s['hotRank-item-detail']}}">
                            <div
                                class="{{s['hotRank-item-title']}}"
                                style="{{theme | getTitleColorStyle(item)}} {{titleFontSize}}">{{item.data.title}}</div>
                            <div
                                s-if="item.data.delegation || item.data.discipline"
                                class="{{s['hotRank-item-meta']}}"
                                style="{{theme | getMetaColorStyle(item)}}">
                                <span s-if="item.data.delegation">{{item.data.delegation}}</span>
                                <span
                                    s-if="item.data.delegation && item.data.discipline"
                                    class="{{s['hotRank-item-split']}}">|</span>
                                <span s-if="item.data.discipline">{{item.data.discipline}}</span>
                            </div>
                        </div>
                    </div>
                    <div s-if="item.data.hot_num">
                        <div
                            class="{{s['hotRank-item-degree']}}"
                            style="{{theme | getDegreeColorStyle(item)}}">{{item.data.hot_num}}</div>
                        <div class="{{s['hotRank-degree']}}" style="{{theme | getDegreeOuterBgStyle(item)}}">
                            <div
                                class="{{s['hotRank-degree-inner']}}"
                                style="width: {{item.data.ratio}}; {{theme | getDegreeInnerBgStyle(item)}}"></div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    `;
    /* eslint-enable max-len */
    static computed = {
        // 标题栏高度等比变化 1242:150
        getComputedHeight() {
            const isFirst = this.data.get('isFirst');
            const width = document.documentElement.clientWidth;
            // const paddingTop = width * 150 / 1242 / 16;
            return `padding-top: ${isFirst ? 0.5625 : 0.8125}rem; padding-bottom: 0.8125rem;`;
        },
        itemData() {
            return this.data.get('feed.data') || {};
        },
        boardList() {
            const listData = this.data.get('itemData').board_list;
            return listData.slice(0, 5);
        },
        titleFontSize() {
            const fontLevel = +this.data.get('fontLevel');
            let result = '';
            switch (fontLevel) {
                case 0:
                    result = 'font-size: 0.875rem';
                    break;
                case 1:
                    result = 'font-size: 1.0625rem';
                    break;
                case 2:
                    result = 'font-size: 1.1875rem';
                    break;
                case 3:
                    result = 'font-size: 1.25rem';
                    break;
            }
            return result;
        },
        isFirst() {
            return this.data.get('pos') === 0;
        },
        placeHolder() {
            const theme = this.data.get('theme');
            return this.data.get(`image_holder${theme === 'light' ? '' : '_' + theme}`);
        },
        borderStyle() {
            const theme = this.data.get('theme');
            let color = 'rgba(0,0,0,0.1)';
            if (theme === 'night') {
                color = '#666666';
            } else if (theme === 'dark') {
                color = '#222222';
            }
            let width = (1 / window.devicePixelRatio).toFixed(1);
            return `border:${width ? width : 0.3}px solid ${color}`;
        }
    };

    static filters = {
        // 动态下发标题icon
        getHeaderTitleIcon(theme, itemData) {
            switch (theme) {
                case 'dark':
                    return itemData.board_icon_dark;
                case 'night':
                    return itemData.board_icon_night;
                default:
                    return itemData.board_icon;
            }
        },
        // 动态下发标题颜色
        getHeaderTitleColorStyle(theme, itemData) {
            let style = '';
            switch (theme) {
                case 'dark':
                    style = itemData.board_name_font_dark;
                    break;
                case 'night':
                    style = itemData.board_name_font_night;
                    break;
                default:
                    style = itemData.board_name_font;
            }
            if (style) {
                return `color: ${style};`;
            }
            return '';
        },
        // 动态下发查看更多颜色
        getHeaderMoreColorStyle(theme, itemData) {
            let style = '';
            switch (theme) {
                case 'dark':
                    style = itemData.more_text_font_dark;
                    break;
                case 'night':
                    style = itemData.more_text_font_night;
                    break;
                default:
                    style = itemData.more_text_font;
            }
            if (style) {
                return `color: ${style};`;
            }
            return '';
        },
        // 动态下发查看更多icon
        getHeaderMoreIcon(theme, itemData) {
            let style = '';
            switch (theme) {
                case 'dark':
                    style = itemData.more_icon_dark;
                    break;
                case 'night':
                    style = itemData.more_icon_night;
                    break;
                default:
                    style = itemData.more_icon;
            }
            if (style) {
                return `background-image: url(${style});`;
            }
            return style;
        },
        // 动态下发标题栏背景图
        getHeaderBgStyle(theme, itemData) {
            let style = '';
            switch (theme) {
                case 'dark':
                    style = itemData.title_image_dark;
                    break;
                case 'night':
                    style = itemData.title_image_night;
                    break;
                default:
                    style = itemData.title_image;
            }
            if (style) {
                return `background-image: url(${style});`;
            }
            return style;
        },
        // 动态下发排行背景
        getRankBgStyle(theme, item) {
            let style = '';
            const itemData = item.data;
            switch (theme) {
                case 'dark':
                    style = itemData && itemData.index_bg_color_dark;
                    break;
                case 'night':
                    style = '#ffffff';
                    // style = itemData && itemData.index_bg_color_night;
                    break;
                default:
                    style = itemData && itemData.index_bg_color;
            }
            if (style) {
                return `background-color: ${style};`;
            }
            return style;
        },
        // 动态下发排行字体颜色
        getRankColorStyle(theme, item) {
            let style = '';
            const itemData = item.data;
            switch (theme) {
                case 'dark':
                    style = itemData && itemData.index_font_color_dark;
                    break;
                case 'night':
                    style = 'rgb(221,0,0)';
                    // style = itemData && itemData.index_font_color_night;
                    break;
                default:
                    style = itemData && itemData.index_font_color;
            }
            if (style) {
                return `color: ${style};`;
            }
            return style;
        },
        // 动态下发子项标题颜色
        getTitleColorStyle(theme, item) {
            let style = '';
            const itemData = item.data;
            switch (theme) {
                case 'dark':
                    style = itemData && itemData.title_font_dark_color;
                    break;
                case 'night':
                    style = itemData && itemData.title_font_night_color;
                    break;
                default:
                    style = itemData && itemData.title_font_color;
            }
            if (style) {
                return `color: ${style};`;
            }
            return style;
        },
        // 动态下发子项meta颜色
        getMetaColorStyle(theme, item) {
            let style = '';
            const itemData = item.data;
            switch (theme) {
                case 'dark':
                    style = itemData && itemData.meta_font_dark_color;
                    break;
                case 'night':
                    style = itemData && itemData.meta_font_night_color;
                    break;
                default:
                    style = itemData && itemData.meta_font_color;
            }
            if (style) {
                return `color: ${style};`;
            }
            return style;
        },
        // 动态下发热度字体颜色
        getDegreeColorStyle(theme, item) {
            let style = '';
            const itemData = item.data;
            switch (theme) {
                case 'dark':
                    style = itemData && itemData.hot_num_font_dark;
                    break;
                case 'night':
                    style = itemData && itemData.hot_num_font_night;
                    break;
                default:
                    style = itemData && itemData.hot_num_font;
            }
            if (style) {
                return `color: ${style};`;
            }
            return style;
        },
        // 动态下发进度条底色
        getDegreeOuterBgStyle(theme, item) {
            let style = '';
            const itemData = item.data;
            switch (theme) {
                case 'dark':
                    style = itemData && itemData.hot_icon_dark;
                    break;
                case 'night':
                    style = '#000000';
                    // style = itemData && itemData.hot_icon_night;
                    break;
                default:
                    style = itemData && itemData.hot_icon;
            }
            if (style) {
                return `background-color: ${style};`;
            }
            return style;
        },
        // 动态下发进度条颜色
        getDegreeInnerBgStyle(theme, item) {
            let style = '';
            const itemData = item.data;
            // 夜间颜色无法使用渐变，会被褪色成黑色所以这里单独设置为背景色白色
            if (theme === 'night') {
                return 'background-color: #ffffff;';
            }
            switch (theme) {
                case 'dark':
                    style = itemData && itemData.hot_icon_inner_dark;
                    break;
                // case 'night':
                //     style = itemData && itemData.hot_icon_inner_night;
                //     break;
                default:
                    style = itemData && itemData.hot_icon_inner;
            }
            if (style) {
                return `background-image: ${style};`;
            }
            return 'background-image: linear-gradient(to right, #ff6600, #faa90e);';
        },
        // 动态下发子项之间的间隔线色值
        getBorderColorStyle(theme, item) {
            let style = '';
            const itemData = item.data;
            switch (theme) {
                case 'dark':
                    style = itemData && itemData.border_color_dark;
                    break;
                case 'night':
                    style = itemData && itemData.border_color_night;
                    break;
                default:
                    style = itemData && itemData.border_color;
            }
            if (style) {
                return `border-bottom: .33px solid ${style};`;
            }
            return style;
        },
        // 动态下发卡片背景（图或者颜色）
        getBgStyle(theme, itemData) {
            let bgstyle = '';
            let color = '';
            let result = '';
            switch (theme) {
                case 'dark':
                    bgstyle = itemData && itemData.bg_image_dark;
                    color = itemData && itemData.bg_color_dark;
                    break;
                case 'night':
                    bgstyle = itemData && itemData.bg_image_night;
                    color = itemData && itemData.bg_color_night;
                    break;
                default:
                    bgstyle = itemData && itemData.bg_image;
                    color = itemData && itemData.bg_color;
            }
            if (bgstyle) {
                result += `background-img: url(${bgstyle});`;
            }
            if (color) {
                result += `background-color: ${color};`;
            }
            return result;
        }
    };

    initData() {
        return {
            IS_IOS: env.isIOS,
            s,
            feed: {},
            logFlag: 0,
            image_holder: require('../../assets/image_holder.png'),
            image_holder_night: require('../../assets/image_holder_night.png'),
            image_holder_dark: require('../../assets/image_holder_dark.png'),
        };
    }
    attached() {
        const visible = this.data.get('visibility');
        // 页面一开始就可视的话，进行展现打点
        visible && this.displayLog();
        // 监听可视状态变化
        this.watch('visibility', val => {
            const logFlag = this.data.get('logFlag');
            val && !logFlag && this.displayLog();
        });
    }
    displayLog() {
        const itemData = this.data.get('itemData');
        ubc('event', {
            page: this.data.get('feed.serverName'),
            type: 'show',
            value: itemData.board_name,
            ext: {
                pos: this.data.get('pos'),
                id: this.data.get('tabId')
            }
        });
        this.data.set('logFlag', 1);
    }
    itemClick(e, item) {
        if (item.data && item.data.cmd) {
            invoke(item.data.cmd);
            ubc('event', {
                page: this.data.get('feed.serverName'),
                type: 'clk',
                value: item.data.title || '',
                ext: {
                    id: this.data.get('tabId'),
                    pos: this.data.get('pos')
                }
            });
        }
    }

    onMoreClick() {
        const itemData = this.data.get('itemData');
        const cmd = itemData && itemData.more_cmd;
        if (cmd) {
            invoke(cmd);
            ubc('event', {
                page: this.data.get('feed.serverName'),
                type: 'clk',
                value: 'more',
                ext: {
                    id: this.data.get('tabId'),
                    pos: this.data.get('pos')
                }
            });
        }
    }
}
