const nameMap = {
    newAddTopProvince: {
        title: '新增确诊分布',
        legendInfo: ['本地新增', '境外输入'],
        localDataConf: {
            type: 'bar',
            color: '#0174DF',
            name: '本地新增',
            emphasis: {
                itemStyle: {
                    color: '#81BEF7'
                }
            },
            barWidth: 10 // 设置柱状图的粗细
        },
        overseasConf: {
            type: 'bar',
            color: '#DF0101',
            name: '境外输入',
            barGap: '-100%', // 用于柱状图的重叠
            emphasis: {
                itemStyle: {
                    color: '#F78181'
                }
            },
            barWidth: 10 // 设置柱状图的粗细
        }
    },
    asymptomaticTopProvince: {
        title: '全国 无症状感染者分布',
        legendInfo: []
    },
    trend: {
        '新增无症状': {
            title: '新增无症状趋势',
            button: ['新增', '无症状趋势'],
            legendInfo: '新增无症状',
            type: 'line'
        },
        '新增确诊': {
            title: '新增确诊趋势',
            button: ['新增', '确诊趋势'],
            legendInfo: '新增确诊',
            type: 'line'
        },
        '确诊': {
            legendInfo: '确诊'
        },
        '治愈': {
            legendInfo: '治愈'
        },
        '死亡': {
            legendInfo: '死亡'
        },
        '新增本土': {
            title: '新增本土趋势',
            button: ['新增', '本土趋势'],
            legendInfo: '新增本土',
            type: 'line'
        }
    },
    allForeignTrend: {
        '新增确诊': {
            title: '国外新增确诊',
            button: '新增确诊按钮',
            legendInfo: '新增确诊',
            type: 'line'
        }
    },
    topAddCountry: {
        title: '新增确诊国家TOP10',
        legendInfo: []
    }
};
/*
    数据结构
    最后吐给app.js的是一个数组，数组的每一项格式如下：
    {
        topTitle: '', // 值为国内疫情、本地、海外
        component: [{
            layout: '', // 组件名
            items: [] // items为组件内需要的数据
        }]
    }
    曲线图的items格式有所不同，items数组的每一项为echart图的配置内容
*/

function trans(serverData) {
    if (!serverData || !serverData.data || !serverData.data.tabs) {
        return [];
    }
    let tabsArr = serverData.data.tabs;
    let res = [];

    for (let index = 0; index < tabsArr.length; index++) {
        let tabItem = tabsArr[index];
        let oneRes = {};
        let trendGroup = [];
        oneRes.topTitle = tabItem.tab_title;
        let component = [];
        for (let key of Object.keys(tabItem)) {
            let tabItemValue = tabItem[key];
            if (!tabItemValue) {
                continue;
            }
            let eachItem = {};
            if (key !== 'around' && typeof tabItemValue !== 'object') {
                oneRes[key] = tabItemValue;
                // continue;
            } else {
                if ((key === 'numeralItems'
                    || key === 'notices'
                    || key === 'domesticDistribution'
                    || key === 'topAddCountry') && tabItemValue.length !== 0) {
                    eachItem.key = key;
                    switch (key) {
                        case 'numeralItems':
                            eachItem.layout = 'show-numeral';
                            break;
                        case 'domesticDistribution':
                            eachItem.layout = 'show-report';
                            break;
                        case 'topAddCountry':
                            eachItem.layout = 'bar-chart';
                            break;
                        case 'notices':
                            eachItem.layout = 'show-notice';
                            break;
                        default:
                            break;
                    }
                    if (key === 'numeralItems' && tabItem.link) {
                        eachItem.cmd = tabItem.link;
                    }
                    eachItem.items = tabItemValue;
                } else if ((key === 'caseList' && tabItemValue.area)
                    || (key === 'caseOutsideList' && tabItemValue.length !== 0)) {
                    eachItem.layout = 'show-table';
                    eachItem.key = key;
                    if (key === 'caseList') {
                        eachItem.headItems = ['省市\n地区', '新增\n确诊', '新增\n无症状', '累计\n确诊', '风险\n地区'];
                        eachItem.firstItems = tabItemValue;
                        eachItem.items = tabItemValue.subList;
                        delete tabItemValue.subList;
                    } else {
                        eachItem.headItems = ['地区', '新增', '累计', '治愈', '死亡'];
                        eachItem.items = tabItemValue;
                    }
                } else if ((key === 'allForeignTrend' || key === 'trend')
                    && Object.prototype.toString.call(tabItemValue.list) === '[object Array]'
                    && tabItemValue.list.length !== 0) {
                    eachItem.layout = 'live-chart';
                    eachItem.items = [];
                    eachItem.key = key;
                    let cureAndDeadSeries = [];
                    let cureAndDeadLegend = [];
                    let cureAndDeadXAxis = [];
                    const start = tabItemValue.updateDate.length - 30;
                    // 本地疫情下的趋势图只展示近一个月的数据
                    let updateDate = key === 'trend' ? tabItemValue.updateDate.slice(start) : tabItemValue.updateDate;

                    for (let index = 0; index < tabItemValue.list.length; index++) {
                        let trendOfEachDay = tabItemValue.list[index]; // list中的每一项，包括name和data
                        let dataInList = key === 'trend' ? trendOfEachDay.data.slice(start) : trendOfEachDay.data;
                        if (trendOfEachDay.name === '新增确诊' || trendOfEachDay.name === '新增本土') {
                            let itemsObj = {
                                key,
                                series: []
                            };
                            // trendConfig为当前数据在nameMap中的配置项
                            let trendConfig = nameMap[key][trendOfEachDay.name];
                            itemsObj.title = trendConfig.title;
                            let buttonName = trendConfig.button;
                            buttonName && (itemsObj.buttonName = buttonName);
                            itemsObj.legendData = [trendConfig.legendInfo];
                            itemsObj.xAxisData = updateDate;
                            let seriesConf = {
                                type: 'line',
                                smooth: true,
                                symbolSize: 0.01,
                                lineStyle: {
                                    width: 1.6
                                }
                            };
                            seriesConf.data = dataInList;
                            seriesConf.name = trendConfig.legendInfo;
                            itemsObj.series.push(seriesConf);

                            // items的每一项就是每个chart图的配置内容
                            eachItem.items.push(itemsObj);
                        } else if (trendOfEachDay.name === '确诊' || trendOfEachDay.name === '治愈' || trendOfEachDay.name === '死亡') {
                            cureAndDeadXAxis = updateDate;
                            cureAndDeadLegend.push(nameMap[key][trendOfEachDay.name].legendInfo);
                            let seriesConf = {
                                type: 'line',
                                smooth: true,
                                symbol: 'emptyCircle',
                                symbolSize: 0.01,
                                lineStyle: {
                                    width: 1.6
                                }
                            };
                            seriesConf.data = dataInList;
                            seriesConf.name = trendOfEachDay.name;
                            cureAndDeadSeries.push(seriesConf);
                        }
                    }
                    if (cureAndDeadSeries.length !== 0 && cureAndDeadLegend.length !== 0) {
                        // 将确诊、死亡和治愈放在一个表里
                        let itemsObj = {
                            key,
                            series: []
                        };
                        itemsObj.legendData = cureAndDeadLegend;
                        itemsObj.xAxisData = cureAndDeadXAxis;
                        itemsObj.series = cureAndDeadSeries;
                        itemsObj.title = '累计确诊/治愈/死亡趋势';
                        itemsObj.buttonName = ['确诊/', '治愈/死亡'],
                        eachItem.items.push(itemsObj);
                    }
                } else if (key === 'around' && typeof tabItemValue === 'string') {
                    let jsonStart = tabItemValue.indexOf('(');
                    let jsonEnd = tabItemValue.lastIndexOf(')');
                    let jsonString = tabItemValue.slice(jsonStart + 1, jsonEnd);
                    eachItem.layout = 'show-surround';
                    eachItem.key = key;
                    let resultObj = decodeURIComponent(jsonString);
                    eachItem.items = JSON.parse(resultObj);
                } else if (key === 'trendMonth' && tabItemValue.list.length !== 0) {
                    // 本地疫情下的趋势图只展示近一个月的数据
                    let updateDate = tabItemValue.updateMonth;

                    for (let index = 0; index < tabItemValue.list.length; index++) {
                        let trendOfEachDay = tabItemValue.list[index]; // list中的每一项，包括name和data
                        let dataInList = trendOfEachDay.data;
                        if (trendOfEachDay.name === '新增确诊') {
                            let itemsObj = {
                                key,
                                series: []
                            };
                            // trendConfig为当前数据在nameMap中的配置项
                            let trendConfig = nameMap['trend'][trendOfEachDay.name];
                            itemsObj.title = trendConfig.title;
                            let buttonName = trendConfig.button;
                            buttonName && (itemsObj.buttonName = buttonName);
                            itemsObj.legendData = [trendConfig.legendInfo];
                            itemsObj.xAxisData = updateDate;
                            let seriesConf = {
                                type: 'line',
                                smooth: true,
                                symbolSize: 0.01,
                                lineStyle: {
                                    width: 1.6
                                }
                            };
                            seriesConf.data = dataInList;
                            seriesConf.name = trendConfig.legendInfo;
                            itemsObj.series.push(seriesConf);

                            // items的每一项就是每个chart图的配置内容
                            trendGroup.push(itemsObj);
                        }
                    }
                }
                if (eachItem.key === 'trend') {
                    // 将trendMonth的数据插入到曲线图里
                    eachItem.monthItems = trendGroup;
                }
                if (eachItem.key === 'notices') {
                    // 疫情公告组件强制提升到第一位
                    component.unshift(eachItem);
                } else {
                    component.push(eachItem);
                }
            }
        }
        oneRes.component = component;
        res.push(oneRes);
    }
    return res;
}
export default trans;
