/**
 * @file 
 * @author leizhihao <leizhihao@baidu.com>
 */
/* eslint-disable fecs-camelcase */
import '@/lib/utils/patchSan';
import {Component} from 'san';
import styles from './app.module.less';
import BaseLog from '@baidu/feed-baselog';
import scroll from 'scroll';
import scrollDoc from 'scroll-doc';
import throttle from 'lodash.throttle';
import AucHeader from '@baidu/auc-header';
import AucColumn from '@baidu/auc-column';
import AucTitlebar from '@baidu/auc-titlebar';
import AucNav from '@baidu/auc-nav';
import AucMenuIcon from '@baidu/auc-menu-icon';
import AucImageHeader from '@baidu/auc-image-header';
import AucBackBar from '@baidu/auc-back-bar';
import {env} from '@baidu/auc-base/env';
import {invokeP} from '@baidu/xbox/na/invoke';
import {init} from '@baidu/auc-log/index';
import {pageLog} from '@baidu/auc-log';
import boxx from '@baidu/auc-base/boxx';
import {machiningColor} from '@baidu/auc-base/utils';
import {setPageMode, getPageMode, registerModeChange} from '@/lib/utils/pageMode';
import {initReflowByUrl, openReflowByUrl} from '@/lib/utils/reflowUgc';
import {getInvokeInfo, getMcpCreater} from '@/lib/utils/mcp';
import {startFontSize} from '@/lib/utils/fontSize';
import {setShareInfo} from '@/lib/utils/setShareInfo';
import {getQueryString} from '@/lib/utils/getQueryString';
import {cookie} from '@/lib/utils';

const baselog = new BaseLog();
const scrollPage = scrollDoc();
const pxToRem = value => value / 100 + 'rem';
const bingEventBgs = ['#fe344a'];

// 
try {
    (env.isBoxSeries || env.isMatrix) && boxx.call('commonUI.closeLoading');
} catch (e) {
    console.error(e);
}

// env
const isCommonEnv = env.isBoxSeries || env.isXiaoMiBaiPai || env.isMatrix;
const isComLightEnv = isCommonEnv && env.isLightFrame;
// 
const POS_TOP = 'pos_topReflowBtn';
// 
const POS_BOTTOM = 'pos_bottomReflowBtn';
let mcpList = null;
let mcpCreater = getMcpCreater();
const modeMap = {
    lightmode: 'day',
    nightmode: 'night',
    darkmode: 'dark'
};

export default class App extends Component {
    static template = /* html */ `
        <div class="{{styles.mainPage}}" id="mainPage">
            <auc-back-bar 
                s-if="!(env.isBoxSeries || env.isXiaoMiBaiPai || env.isMatrix)"
                on-backShoubai="backShoubai('top')"></auc-back-bar>
            <auc-titlebar
                title="{{head.title}}"
                s-if="showTitleBar"
                s-ref="titleBar"
                on-click-set="clickSet"
                top="{{titleBarTop}}"
                isBigEvent="{{bigEvent.landType !== 0}}"
                bigEventColor="{{bigEventColor}}"
            >
                <div
                    s-if="{{showTitleBar && isImmersive}}"
                    style="height: {{statusBarHeightRem}};{{bigEventColor ? 'background-color:' + bigEventColor : ''}}"
                    class="titleImmersive"
                ></div>
            </auc-titlebar>
            <auc-menu-icon
                s-if="showMenuIcon && (env.isBoxSeries || env.isXiaoMiBaiPai || env.isMatrix)"
                top="{{statusBarHeightRem}}"
                isBigEvent="{{bigEvent.landType !== 0}}"
                on-click-set="clickSet"
            >
            </auc-menu-icon>
            <auc-image-header
                s-if="bigEvent.landTopImgType === 1"
                s-ref="header"
                src="{{head.imgUrl}}"
                imgType="{{head.imgType}}"
                headImgColor={{head.headImgColor}}
                class="{{styles.imgHeader}}"
            ></auc-image-header>
            <auc-header
                s-else
                s-ref="header"
                s-bind="{{{
                    ...head,
                    isBigEvent: bigEvent.landType !== 0,
                    noHeadDesc: noHeadDesc,
                    statusBarHeight: statusBarHeight
                }}}"
            ></auc-header>
            <template s-if="list.showingColumns.length > 1">
                <auc-nav
                    s-if="{{list.navList.length > 1}}"
                    s-ref="navs"
                    id="navs"
                    navs="{{list.navList}}"
                    on-nav-change="currColumnChange"
                    activeIndex="{{activeIndex}}"
                    navDialogTop="{{navDialogTop}}"
                    navInnerTop="{{navInnerTop}}"
                    noMarginTop="{{noHeadDesc}}"
                    isBigEventHeader="{{bigEvent.landTopImgType === 1}}"
                    isPc="{{!env.isMobile}}"
                    landType="{{bigEvent.landType}}"
                    titleBarHeight="{{titleBarHeight}}"
                    statusBarHeight="{{statusBarHeight}}"
                    bigEventColor="{{bigEventColor}}"
                    navColor="{{navColor}}"
                    on-change-fixed="changeFixed"
                    on-more-click="navMoreClick"
                    isTransparentWrap="true"
                    dialogTitle=""
                ></auc-nav>
            </template>
            <auc-column
                id="column{{index}}"
                class="auc-column-watch {{specialCloumnClass}} {{column.columnNameType && bigEvent.landType !== 0? 'image-name' : ''}}"
                s-for="column,index in list.frontShowClolumn"
                showTitle="{{list.frontShowClolumn.length > 1}}"
                s-bind="column"
                box-prase-icon-list="{{boxPraseIconList}}"
                page-mode="{{pageMode}}"
                column-index="{{index}}"
                is-immersive="{{isImmersive}}"
                status-bar-height="{{statusBarHeight}}"
                globalVedioMute="{{globalVedioMute}}"
                globalVedioAutoplay="{{globalVedioAutoplay}}"
                globalVedioVolume="{{globalVedioVolume}}"
                networtType="{{networtType}}"
                isBigEvent="{{bigEvent.landType !== 0}}"
                landType="{{bigEvent.landType}}"
                statusBarHeight="{{statusBarHeight}}"
                titleBarHeight="{{titleBarHeight}}"
                topicId="{{info.id}}"
                isHotNews="true"
                on-click-more="clickMore(index)"
                on-comJump="comJump"
                on-backShoubai="backShoubai('bottom')"
            ></auc-column>
        </div>
    `;

    static components = {
        'auc-header': AucHeader,
        'auc-column': AucColumn,
        'auc-nav': AucNav,
        'auc-titlebar': AucTitlebar,
        'auc-menu-icon': AucMenuIcon,
        'auc-image-header': AucImageHeader,
        'auc-back-bar': AucBackBar,
    };

    static computed = {
        showTitleBar() {
            if (isComLightEnv) {
                return this.data.get('appShowTitleBar');
            }
        },
        statusBarHeightRem() {
            // iOS
            return pxToRem(+this.data.get('statusBarHeight') + 7);
        },
        showMenuIcon() {
            if (isComLightEnv) {
                return !this.data.get('showTitleBar');
            }
        },
        navInnerTop() {
            const titleBarHeight = this.data.get('titleBarHeight');
            const statusBarHeight = this.data.get('statusBarHeight');
            if (isComLightEnv) {
                if (this.data.get('isImmersive')) {
                    return pxToRem(titleBarHeight + statusBarHeight);
                } else {
                    return pxToRem(titleBarHeight);
                }
            } else {
                return 0;
            }
        },
        navDialogTop() {
            const titleBarHeight = this.data.get('titleBarHeight');
            const statusBarHeight = this.data.get('statusBarHeight');
            if ((env.isBoxSeries || env.isXiaoMiBaiPai || env.isMatrix) && env.isLightFrame) {
                if (this.data.get('isImmersive')) {
                    return pxToRem(titleBarHeight + statusBarHeight);
                } else {
                    return pxToRem(titleBarHeight);
                }
            } else {
                return 0;
            }
        },
        titleBarTop() {
            if (!isCommonEnv) {
                return 0;
            } else if (this.data.get('isImmersive')) {
                return pxToRem(this.data.get('statusBarHeight'));
            }
        },
        titleBarHeight() {
            if (!isCommonEnv) {
                return 0;
            } else {
                return +this.data.get('defaultTitleBarHeight');
            }
        },

        hotReviewsPlaceholder() {
            const clicked = this.data.get('commentList.clicked');
            return [
                styles.hotReviewsPlaceholder,
                clicked ? styles.clicked : ''  
            ]
        },
        pageArr() {
            const showingColumns = this.data.get('list.showingColumns');
            if (!showingColumns?.length) {
                return;
            }
            // 
            return [...Array(showingColumns?.length)].map(_=>1);
        }
    };

    initData() {
        return {
            styles,
            title: '',
            columnPositon: [],
            activeIndex: 0,
            columns: [],
            isImmersive: false,
            env,
            pageSize: 20, // 20
            pageArr: [],
            isInit: false,
            appShowTitleBar: false,
            scrolling: false, // 
            defaultStatusBarHeight: 35, // 
            statusBarHeight: 0, // 
            defaultTitleBarHeight: 44, // titlebar TODO: 
            preActiveIndex: 0, // tabIndex
            noHeadDesc: false,
            pageMode: 'day',
            boxPraseIconList: {},
            globalVedioMute: true,
            globalVedioAutoplay: 0,
            globalVedioVolume: 0,
            networtType: 'wifi',
            navColor: '',
            videoList: {},
            timeouter: null,
            invokeInfo: {},
            tempHotReviewsVal: false,
        };
    }

    async inited() {
        let isTomas = /tomas/i.test(env.ua);
        (env.isBoxSeries || env.isXiaoMiBaiPai || env.isMatrix) && !isTomas && env.isIOS && startFontSize();
        this.initLog();
        this.processing();
        this.watchMode();
        // landType = 0 
        this.data.get('bigEvent.landType') !== 0 && this.bigEventConfig();

        this.watch('appShowTitleBar', (val) => {
            if (this.data.get('tempHotReviewsVal') === val) {
                return;
            }
            this.data.set('tempHotReviewsVal', val);
        });
    }

    initInvoke() {
        // url
        let {link: url = window.location.href} = window.sortsData.header || {};
        // url
        !/isimmersive/.test(url) ? url += '&isimmersive=immersive': '';
        const params = {
            baiduid: cookie('BAIDUID'),
            'user_agent': navigator.userAgent,
            'invoke_info': {
                [POS_TOP]: [{
                    url
                }],
                [POS_BOTTOM]: [{
                    url
                }]
            }
        };
        getInvokeInfo(params).then(res => {
            this.data.set('invokeInfo', res);
            let actionRule = res.action_rule;
            mcpList = Object.keys(actionRule).map(key => {
                return {
                    pos: key,
                    invokeMcp: mcpCreater.create({
                        pos: key,
                        opt: actionRule[key],
                        logId: res.log_id
                    })
                };
            });
        });
    }

    attached() {
        this.pageShowLog();

        // 
        this.data.set(
            'isImmersive',
            getQueryString('isimmersive') === 'immersive'
        );

        // 
        const lastIndex = this.data.get('list').allColumns.length - 1;
        window.addEventListener('scroll', throttle(() => {
            // tab
            this.checkNavIndex();

            // titlebar
            this.checkShowTitleBar();

            // 
            this.checkScrollBottom();

            // tab
            if (window.scrollY + window.innerHeight + 5 >= document.body.offsetHeight) {
                this.data.set('activeIndex', lastIndex);
                this.filterbtnStyle(lastIndex);
            }
            // titlebarnav
            if (window.scrollY < 10) {
                this.data.set('appShowTitleBar', false);
                this.ref('navs') && this.ref('navs').maskClick();
            }
        }, 100));

        // &oppopc 
        let ispiyao = +getQueryString('id') === 5807;
        let isKnews = getQueryString('_refluxos') === 'a8';
        if (!env.isBoxSeries && !(env.isHuawei && ispiyao)
            && !env.isOppo && env.isMobile && !isKnews && !env.isXiaoMiBaiPai && !env.isMatrix) {
            this.initInvoke();
            initReflowByUrl(window.location.href);
        }

        this.titleBarOffsetSet().then(value => {
            this.data.set('statusBarHeight', +value);
        });

        const {
            title,
            description = '',
            img_url,
            link
        } = window.sortsData.header || {};
        setShareInfo({
            title,
            link,
            description,
            icon: img_url
        });
    }

    // 
    async bigEventConfig() {
        const landType = this.data.get('bigEvent.landType');
        const landBgColor = machiningColor(this.data.get('bigEvent.landBgColor'));
        let bigEventColor = bingEventBgs[landType - 1];
        landBgColor ? bigEventColor = landBgColor : '';
        this.data.set('bigEventColor', bigEventColor);
        const navs = this.data.get('list.navList');
        // navColorbgColor
        const navColor = machiningColor(this.data.get('bigEvent.landNavigationColor'));
        navColor && this.data.set('navColor', navColor);
        navs && navs.forEach((item, index) => {
            let btnStyle = '';
            if (navColor) {
                btnStyle = `background: none;color: ${navColor};`;
                if (index === this.data.get('activeIndex')) {
                    btnStyle = `color: ${navColor};background: none;`;
                }
            } else {
                btnStyle = 'background: rgba(255,255,255,0.14);color: #fff;';
                if (index === this.data.get('activeIndex')) {
                    btnStyle = `color: ${bigEventColor};background: #fff;`;
                }
            }
            return item.btnStyle = btnStyle;
        });
    }

    // 
    async watchMode() {
        if (env.isBoxSeries || env.isXiaoMiBaiPai || env.isMatrix) {
            setPageMode();
            const pageMode = await getPageMode();
            this.data.set('pageMode', modeMap[pageMode]);
            registerModeChange((mode) => {
                this.data.set('pageMode', modeMap[mode]);
            });
        }
    }

    // 
    // 
    checkScrollBottom() {
        // +5case
        if (!this.data.get('logPageEnd') && window.scrollY + window.innerHeight + 5 >= document.body.offsetHeight) {
            this.data.set('logPageEnd', true);
            pageLog('finish');
        }
    }

    columnShow() {
        pageLog('field_show');
    }

    // 
    initLog() {
        init([
            // 
            {
                event: 'page',
                ubcId: '11431',
                params: {
                    page: 'hot_news',
                    source: getQueryString('source') || 'other',
                }
            },
            // 
            {
                event: 'component',
                ubcId: '11455',
                params: {
                    page: 'hot_news',
                    source: getQueryString('source') || 'other',
                }
            },
        ]);

        // 
        const ubcLogger = baselog.ubcInit(16834);
        ubcLogger.startTimingLog('hot_news_timing',{
            page: 'hot_news',
        });
        // feed
        const ubcLoggerFeed = baselog.ubcInit(507);
        ubcLoggerFeed.startTimingLog('hot_news_timing',{
            frame_source: 'hot_news',
        });
    }

    pageShowLog() {
        // 
        pageLog('page_show', {
            enter_nid: getQueryString('nid') || ''
        });
        // feed
        baselog.sendUbc({
            ubcId: '5946',
            params: {
                source: 'hot_news',
                from: 'feed',
            },
        });
        // thunder
        baselog.sendLog({
            ct: 3,
            cst: 1,
            logFrom: "feed_midbox",
            logInfo: "a2_1_how_news",
        });
    }

    //  TODO
    titleBarOffsetSet() {
        return new Promise((resolve, reject) => {
            if (!this.data.get('isImmersive')) {
                resolve(0);
                return;
            }
            if (env.isBoxSeries || env.isMatrix) {
                if (env.isIOS) {
                    invokeP('v16/utils/getStatusBarHeight').then(res => {
                        const height = +res.height;
                        resolve(height);
                    }, () => {
                        resolve(this.data.get('defaultStatusBarHeight'));
                    });
                } else {
                    resolve(this.data.get('defaultStatusBarHeight'));
                }
            }
        });
    }

    // titleBartab10
    checkShowTitleBar() {
        this.data.set(
            'appShowTitleBar',
            window.scrollY > 10
        );
    }

    // tab
    checkNavIndex() {
        if (this.data.get('scrolling')) {
            return;
        }
        const statusBarHeight = this.data.get('statusBarHeight');

        const columns = document.getElementsByClassName('auc-column-watch');
        let result = 0;
        const navs = this.ref('navs');
        if (navs) {
            let nav = navs.children[1];
            let navHeight = nav.el.getBoundingClientRect().height;
            let topBarHeight = this.data.get('titleBarHeight');
            let scrollY = window.scrollY;
            for (let i = 0; i < columns.length; i++) {
                let column = columns[i];
                // 
                let defaultOffset = /image-name/.test(column.className) ? 30 : 20;
                if (column.offsetTop - navHeight - topBarHeight - statusBarHeight - defaultOffset < scrollY) {
                    result = i;
                } else {
                    break;
                }
            }

            this.logTabSwitch(result, 'scroll');
            this.data.set('activeIndex', result);
            this.filterbtnStyle(result);
        }
    }

    // tab
    logTabSwitch(index, switchType = 'click') {
        if (index === this.data.get('preActiveIndex')) {
            return;
        }
        this.data.set('preActiveIndex', index);

        pageLog('tab_click', {
            column_id: index + 1,
            switch_type: switchType
        });
    }

    // navbar fixed
    changeFixed() {
        this.checkShowTitleBar();
    }

    commonLoad(index, loadingState, timeout) {
        setTimeout(() => {
            this.storeDispatch('setColumnLoadingState', {
                index,
                loadingState
            });
        }, timeout);
    }

    // 
    comJump() {
        // 
        baselog.sendLog({
            ct: 3,
            cst: 2,
            logFrom: "feed_midbox",
            logInfo: "a2_1_how_news",
        });
    }

    // 
    clickMore(index) {
        // 
        this.commonLoad(index, 1, 0);

        // pageindex
        let pageArr = this.data.get('pageArr');
        pageArr[index] = pageArr[index] += 1;

        const pageSize = this.data.get('pageSize');
        const beginNews = pageArr[index] * pageSize;

        let showingColumns = this.data.get('list.showingColumns');
        // column
        let copyColumns = JSON.parse(JSON.stringify(showingColumns));
        // newsList
        const nextNewsList = showingColumns[index].newsList.slice(0, beginNews);
        copyColumns[index].newsList.splice(0, copyColumns[index].newsList.length, ...nextNewsList);

        this.commonLoad(index, 0, 300);

        if (copyColumns[index].newsList.length === showingColumns[index].newsList.length) {
            copyColumns[index].isEnd = 1;
        };

        // splice
        this.data.splice('list.frontShowClolumn', [index, 1, copyColumns[index]]);
    }

    // 
    clickSet() {
        boxx.call('easyBrowser.showMenu');
    }

    // 
    scroll(top) {
        return new Promise(resolve => {
            scroll.top(scrollPage, top, {
                duration: 150, // 
                ease: n => n // 
            }, resolve);
        });
    }

    // tabactiveIndex
    currColumnChange(index) {
        // 
        if (this.data.get('scrolling')) {
            return;
        }
        this.logTabSwitch(index, 'click');
        let navHeight = this.ref('navs').children[1].el.getBoundingClientRect().height;
        const topBarHeight = this.data.get('titleBarHeight');
        const statusBarHeight = this.data.get('statusBarHeight');
        let offsetTop = document.getElementById('column' + index).offsetTop;
        // 
        let defaultOffset = env.isAndroid ? 0 : 10;
        if (document.getElementById('column' + index) && /image-name/.test(document.getElementById('column' + index).className)) {
            // 25
            defaultOffset = 25;
        }
        // dis
        let dis = offsetTop - navHeight - topBarHeight - statusBarHeight - defaultOffset;
        this.data.set('scrolling', true);
        this.data.set('activeIndex', index);
        this.scroll(dis).then(() => {
            // scrolling
            setTimeout(() => {
                this.data.set('scrolling', false);
            }, 30);
        });
        this.filterbtnStyle(index);
    }

    filterbtnStyle(activeIndex) {
        const navs = this.data.get('list.navList');
        const navColor = machiningColor(this.data.get('bigEvent.landNavigationColor'));
        navs && navs.forEach((item, index) => {
            let btnStyle = '';

            btnStyle = `background: none;color: ${navColor};`;
            if (index === this.data.get('activeIndex')) {
                btnStyle = `color: ${navColor};background: none;`;
            }

            this.data.set(`list.navList[${index}].btnStyle`, btnStyle);
        });
    }

    // 
    processing() {
        // imgType=0||1navmargin-top
        let head = this.data.get('head');
        const modifyTime = this.data.get('modifyTime');
        let isSecondEdition = modifyTime > 1599567000 && modifyTime < 1626852919;
        if (head && !head.description && !(head.imgType === 2 && isSecondEdition)) {
            this.data.set('noHeadDesc', true);
        }

        const allColumns = this.data.get('list.allColumns');
        const showingColumns = this.data.get('list.showingColumns');

        if (allColumns.length === showingColumns.length) {
            this.storeDispatch('main:setLastColumn');
        }
    }

    // 
    navMoreClick() {
        const statusBarHeight = this.data.get('statusBarHeight');
        let topBarHeight = this.data.get('titleBarHeight');
        const navsHeight = this.ref('navs') ? this.ref('navs').el.getBoundingClientRect().height : 0;
        // 
        if (this.data.get('scrolling')) {
            return;
        }
        this.data.set('scrolling', true);
        const dis =  document.getElementById('column0').offsetTop - navsHeight - statusBarHeight - topBarHeight;
        this.scroll(dis).then(() => {
            // scrolling
            setTimeout(() => {
                this.data.set('scrolling', false);
            }, 30);
        });
    }

    // 
    backShoubai(pos) {
        pageLog('topic_back_shoubai');
        if (mcpList) {
            let posId = pos === 'top' ? POS_TOP : POS_BOTTOM;
            let mcpInstance = mcpList.find(item => item.pos === posId)?.invokeMcp;
            const {isWeixin, isIOS, isSafari} = env;
            const hitMcp = mcpInstance && mcpInstance.hitMcp() && mcpInstance.getActionFlag();
            // iossafarimcpmcpulink
            // fixqqulink
            const useMcp = hitMcp && ((isIOS && (isWeixin || isSafari)) || !isIOS);
            if (mcpInstance && !mcpInstance.getActionFlag()) {
                return;
            }
            if (useMcp) {
                // : true:, false:
                mcpInstance.index = 0;
                mcpInstance.execInvoke();
            } else {
                openReflowByUrl(window.location.href);
            }
        } else {
            openReflowByUrl(window.location.href);
        }
    }
}
