/**
 * @file 
 * @author leizhihao <leizhihao@baidu.com>
 */
/* eslint-disable fecs-camelcase */
import {Component} from 'san';
import ubc from '@baidu/ubc-report-sdk';
import axios from 'axios';
import {env} from '@baidu/auc-base/env';
import styles from './app.module.less';
import trans from './trans.js';
import SurroundEpid from '../components/surroundEpid';
import invokeScheme from '../utils/invokeScheme';
import channelize from './each-item';
import SpyClient from 'spy-client/dist/spy-client.min';
import versionCompare from '@baidu/xbox/version_compare';
// const aaa = require('./mock.json');
const EachItem = channelize();
// 
const shareText = ['', '', ''];
// 
const pageSign = ['domestic', 'local', 'overseas'];
// widget
const widgetShare = {
    ios: 'https://motion.baidu.com/activity/widget/ios',
    android: 'https://mbd.baidu.com/baiduapp/widget/android'
};
export default class App extends Component {

    static components = {
        'each-item': EachItem,
        'surround': SurroundEpid
    };
    /* eslint-disable max-len */
    static template = `
        <div id="appContain" class="{{styles['app']}}">
            <div style="padding: 0 {{partialData ? 0 : IS_IOS ? '15px' : '12px'}};">
                <div class="{{styles['app-container']}}"
                    style="{{partialData ? 'border-radius: 0px;' : ''}}">
                    <div s-if="{{!partialData && widget}}" s-ref="widget"
                        class="{{styles['app-container-widget']}}"
                        style="background-image: url({{widgetBackground}})"
                    >
                        <div class="{{styles['app-container-widget-title']}}">
                            {{widget.widget_title}}
                        </div>
                        <div class="{{styles['app-container-widget-button']}}"
                            on-click="kangyiWidgetButtonClick"
                        >
                            <span style="background-image: url({{widgetButtonBackground}})">
                                {{widget.button_text}}
                            </span>
                        </div>
                    </div>
                    <div s-if="{{!partialData}}"
                        class="{{styles['app-container-alltabs']}} {{widget ? styles['has-widget'] : ''}}">
                        <div class="{{styles['app-container-tabItem']}}"
                            s-for="item, index in topTitle"
                            on-click="topBarClick(index)"
                            class="{{index === topTabSelectedId
                                ? styles['app-container-tabItem-active'] : styles['app-container-tabItem']}}">
                            <span>{{item}}</span>
                        </div>
                    </div>
                    <div class="">
                        <div>
                            <each-item
                                s-for="feed, index in currentSelectData.component"
                                feed="{{feed}}"
                                index="{{index}}"
                                province="{{currentSelectData.province || ''}}"
                                timeInfo="{{currentSelectData.update_time || ''}}"
                                theme="{{theme}}"
                                topTabSelectedId="{{topTabSelectedId}}"
                                tabType="{{currentSelectData.tab_type}}"
                                tabTitle="{{currentSelectData.tab_title}}"
                                underTalosTab="{{partialData}}"/>
                        </div>
                    </div>
                    <div s-if="{{!partialData}}"
                        class="{{styles['app-operate']}}" style="{{topTabSelectedId !== 0 ? 'margin-top: .3125rem': ''}}"
                    >
                        <div class="{{styles['app-operate-detail']}}"
                            on-click="showDetail">
                            <img src="{{detailIcon}}" />
                            {{topTabSelectedId === 0 ? '' : '' + topTitle[topTabSelectedId]}}
                        </div>
                        <div class="{{styles['app-operate-share']}}"
                            on-click="shareClick">
                            <img src="{{shareIcon}}"/>
                            {{topTabSelectedId === 0 ? '' : '' + topTitle[topTabSelectedId]}}
                        </div>
                    </div>
                </div>
            </div>
            <div s-if="{{!partialData}}" class="{{styles['app-gradient']}}"></div>
        </div>
    `;
    /* eslint-enable max-len */

    static filter = {
    }
    static messages = {
        'report:reportTableHasShow'() {
            this.data.set('reportTableHasShow', true);
        }
    }
    static computed = {
        topTitle() {
            const feed = this.data.get('feed');
            return feed && feed.map((item, index) => {
                return item.topTitle || '';
            });
        },
        topTabSelectedId() {
            const feed = this.data.get('feed');
            let defaultIndex = 0;
            for (let index = 0; index < feed.length; index++) {
                if (feed[index].is_default && +feed[index].is_default === 1) {
                    defaultIndex = index;
                    return defaultIndex;
                }
            }
            return defaultIndex;
        },
        appWidth() {
            let space = env.isIOS ? 15 : 12;
            return document.documentElement.clientWidth - space * 2;
        },
        widget() {
            const {ua = '', isAndroid} = env;
            const {bd_version = ''} = this.data.get('requestParams');
            if (
                isAndroid && versionCompare(bd_version, '13.10') >= 0
            ) {
                return this.data.get('data.widget') || {};
            }
            return '';
        },
        widgetBackground() {
            const widget = this.data.get('data.widget') || {};
            const theme = this.data.get('theme');
            switch (theme) {
                case 'kangyi-dark':
                    return widget.background_dark;
                case 'kangyi-night':
                    return widget.background_night;
                default:
                    return widget.background;
            }
        },
        widgetButtonBackground() {
            const widget = this.data.get('data.widget') || {};
            const theme = this.data.get('theme');
            switch (theme) {
                case 'kangyi-dark':
                    return widget.button_icon_dark;
                case 'kangyi-night':
                    return widget.button_icon_night;
                default:
                    return widget.button_icon;
            }
        },
        detailIcon() {
            const theme = this.data.get('theme');
            switch (theme) {
                case 'kangyi-light':
                    return require('../assets/detail.png');
                case 'kangyi-night':
                    return require('../assets/detail_night.png');
                case 'kangyi-dark':
                    return require('../assets/detail_dark.png');
                default:
                    return require('../assets/detail.png');
            }
        },
        shareIcon() {
            const theme = this.data.get('theme');
            switch (theme) {
                case 'kangyi-light':
                    return require('../assets/share.png');
                case 'kangyi-night':
                    return require('../assets/share_night.png');
                default:
                    return require('../assets/share.png');
            }
        },
        currentSelectData() {
            let topTabSelectedId = this.data.get('topTabSelectedId');
            const feed = this.data.get('feed');
            const feedLen = feed.length;
            if (feedLen > 0) {
                if (topTabSelectedId >= feedLen) {
                    topTabSelectedId = 0;
                }
                return feed[topTabSelectedId];
            }
            return null;
        }
    };

    // 
    showDetail() {
        const topTabSelectedId = this.data.get('topTabSelectedId');
        let schemeHead = 'baiduboxapp://v1/browser/open?url=';
        let skipUrl = this.data.get('currentSelectData').link;
        if (skipUrl) {
            let couponUrl = schemeHead + encodeURIComponent(skipUrl);
            invokeScheme(couponUrl);
        }
        // 
        ubc('event', {
            page: pageSign[topTabSelectedId],
            type: 'clk',
            value: 'show detail'
        });
    }

    //  widget button 
    kangyiWidgetButtonClick() {
        const widget = this.data.get('data.widget') || {};
        const {widget_cmd = ''} = widget;

        invokeScheme(widget_cmd);

        // 
        ubc('event', {
            page: 'widget',
            type: 'clk',
            source: 'feedky'
        });
    }

    // 
    shareClick() {
        const topTabSelectedId = this.data.get('topTabSelectedId');
        let shareInfo = {
            title: shareText[topTabSelectedId],
            url: '',
            image: ''
        };
        let skipUrl = this.data.get('currentSelectData').link;
        shareInfo.url = skipUrl || '';
        window.ReactNativeWebView && window.ReactNativeWebView.postMessage(JSON.stringify(shareInfo));
        // 
        ubc('event', {
            page: pageSign[topTabSelectedId],
            type: 'clk',
            value: 'share'
        });
    }

    // bar
    topBarClick(index) {
        this.data.set('topTabSelectedId', index);
        let logPageArr = this.data.get('logPageArr');
        logPageArr[index] && ubc('event', {
            type: 'display',
            page: logPageArr[index]
        });
        logPageArr[index] = '';
        this.data.set('logPageArr', logPageArr);
        this.nextTick(() => {
            // tab
            let height = document.getElementById('appContain').scrollHeight;
            // ios1px
            window.ReactNativeWebView && window.ReactNativeWebView.postMessage(height - 1);
        });
    }

    initLog() {
        ubc('init', '2988', {
            timingId: '11656',
            page: 'home', // 
            from: 'feed',
            autoPvTrack: true,
            source: 'feed'
        });
        // ubc('timing', 'start');
    }

    inited() {
        this.initLog();
    }
    created() {
        // urlhtml
        let paramsFromUrl = window.location.href;
        let themeIndex = paramsFromUrl.indexOf('&theme=');
        let paramStart = paramsFromUrl.indexOf('?');
        let paramEnd = paramsFromUrl.indexOf('&timeStamp') === -1
            ? paramsFromUrl.length : paramsFromUrl.indexOf('&timeStamp');
        // rc-viewurlthemeparamEnd
        let theme = paramsFromUrl.slice(themeIndex + 1, paramEnd).split('=')[1];
        const $root = document.getElementsByTagName('html')[0];
        $root.classList.add(theme);
        this.data.set('theme', theme);
        // 
        if (/iphone|ipad|ipod|ios/ig.test(paramsFromUrl)) {
            $root.classList.add('iphone');
        } else {
            $root.classList.add('android');
        }
        // 
        let requestStr = paramsFromUrl.slice(paramStart + 1, themeIndex);
        let requestParams = {};
        if (requestStr.indexOf('&') !== -1) {
            requestStr.split('&').forEach((item, index) => {
                if (!item || !item.split || item.split('=').length !== 2) {
                    return;
                }
                let key = item.split('=')[0];
                let val = item.split('=')[1];
                requestParams[key] = decodeURIComponent(val);
            });

            this.data.set('requestParams', requestParams);
            this.data.set('partialData', requestParams.partialData);
        }
        if (window.sortsData.error !== 0) {
            window.ReactNativeWebView && window.ReactNativeWebView.postMessage(0);
        } else {
            this.data.set('feed', trans(window.sortsData));
        }
        // this.data.set('feed', trans(aaa));
    }

    attached() {
        window.SP && window.SP.mark('attached');
        this.perfLog();
        window.addEventListener('resize', () => {
            let space = env.isIOS ? 15 : 12;
            let newAppWidth = document.documentElement.clientWidth - space * 2;
            if (this.data.get('appWidth') === newAppWidth) {
                return;
            }
            this.data.set('appWidth', newAppWidth);
        });
        // iOS
        window.addEventListener('message', e => {
            if (!e.data || typeof e.data !== 'string' || typeof e.data === 'string' && e.data.indexOf('HN') === -1) {
                return;
            }
            let dataFromHN = JSON.parse(e.data);
            if (dataFromHN.from !== 'HN') {
                return;
            }
            if (dataFromHN.operate === 'refresh') {
                // 
                if (!dataFromHN.paramToH5 || dataFromHN.paramToH5.indexOf('&') === -1) {
                    this.sendRequest();
                } else {
                    let newRequestParams = {};
                    // paramToH5
                    dataFromHN.paramToH5.split('&').forEach((item, index) => {
                        if (!item || !item.split || item.split('=').length !== 2) {
                            return;
                        }
                        let key = item.split('=')[0];
                        let val = item.split('=')[1];
                        newRequestParams[key] = decodeURIComponent(val);
                    });
                    this.sendRequest(newRequestParams);
                    this.data.set('requestParams', newRequestParams);
                }
            } else if (dataFromHN.theme) {
                // 
                const theme = `kangyi-${dataFromHN.theme}`;
                const IS_IOS = this.data.get('IS_IOS');
                const $root = document.getElementsByTagName('html')[0];
                $root.className = `${theme} ${IS_IOS ? 'iphone' : 'android'}`;
                this.data.set('theme', theme);
            } else if(dataFromHN.index) {
                const index = dataFromHN.index;
                if (+index !== 0) {
                    this.data.set('topTabSelectedId', +index);
                    setTimeout(() => {
                        let height = document.getElementById('appContain').scrollHeight;
                        console.log('height',height);
                        window.ReactNativeWebView && window.ReactNativeWebView.postMessage(height);
                    }, 300)
                }
            }
        });
        // android
        document.addEventListener('message', e => {
            let dataFromHN = JSON.parse(e.data);
            if (dataFromHN.from !== 'HN') {
                return;
            }
            if (dataFromHN.operate === 'refresh') {
                // 
                if (!dataFromHN.paramToH5 || dataFromHN.paramToH5.indexOf('&') === -1) {
                    this.sendRequest();
                } else {
                    let newRequestParams = {};
                    // paramToH5
                    dataFromHN.paramToH5.split('&').forEach((item, index) => {
                        if (!item || !item.split || item.split('=').length !== 2) {
                            return;
                        }
                        let key = item.split('=')[0];
                        let val = item.split('=')[1];
                        newRequestParams[key] = decodeURIComponent(val);
                    });
                    this.sendRequest(newRequestParams);
                    this.data.set('requestParams', newRequestParams);
                }
            } else if (dataFromHN.theme) {
                // 
                const theme = `kangyi-${dataFromHN.theme}`;
                const IS_IOS = this.data.get('IS_IOS');
                const $root = document.getElementsByTagName('html')[0];
                $root.className = `${theme} ${IS_IOS ? 'iphone' : 'android'}`;
                this.data.set('theme', theme);
            } else if(dataFromHN.index) {
                const index = dataFromHN.index;
                if (+index !== 0) {
                    this.data.set('topTabSelectedId', +index);
                    this.nextTick(() => {
                        let height = document.getElementById('appContain').scrollHeight;
                        window.ReactNativeWebView && window.ReactNativeWebView.postMessage(height - 1);
                    });
                }
            } else if (dataFromHN.visible) {
                if (dataFromHN.visible === 'false') {
                    ubc('timing', 'end');
                } else {
                    ubc('timing', 'start');
                }
            }
        });
        // webview
        this.nextTick(() => {
            const requestParams = this.data.get('requestParams');
            let height = document.getElementById('appContain').scrollHeight;
            window.ReactNativeWebView && window.ReactNativeWebView.postMessage(height);
            // 
            if (requestParams.tabVisible === 'true') {
                let logPageArr = this.data.get('logPageArr');
                const topTabSelectedId = this.data.get('topTabSelectedId');
                logPageArr[topTabSelectedId] && ubc('event', {
                    type: 'display',
                    page: logPageArr[topTabSelectedId]
                });
                // widget
                this.ref('widget') && ubc('event', {
                    type: 'display',
                    page: 'widget',
                    source: 'feedky'
                });
                // logPageArr
                logPageArr[topTabSelectedId] = '';
                this.data.set('logPageArr', logPageArr);
                // 
                ubc('timing', 'start');
            }
        });
    }
    perfLog() {
        const spy = new SpyClient({
            pid: '10_133',
            sample: 1
        });
        const dim = {
            os: env.isAndroid ? 'android' : env.isIOS ? 'ios' : 'other',
            appVersion: env.boxVersion
        };
        const sendPerf = info => {
            spy.sendPerf({
                info: info,
                group: 'kangyi_perf',
                dim
            });
        };
        // lcp
        spy.listenLCP(sendPerf);
        // fid
        spy.listenFID(sendPerf);
        // tti
        spy.listenTTI(sendPerf);

        // spy
        spy.listenTiming(metric => {
            let customPerf = {};
            const marks = window.SP.data.marks;
            for (let i in marks) {
                customPerf[i] = window.SP.cal(i);
            }
            sendPerf(Object.assign({}, metric, customPerf));
        });
    }

    sendRequest(query) {
        let requestParams = query || this.data.get('requestParams');
        axios.get('/newspage/data/epidemic', {
            params: {
                ...requestParams,
                _format: 'json'
            }
        }).then(res => {
            if (res.status === 200) {
                const data = res.data;
                const topTabSelectedId = this.data.get('topTabSelectedId');
                let newFeed = trans(data);
                for (let index = 0; index < newFeed.length; index++) {
                    index === topTabSelectedId ? (newFeed[index].is_default = 1) : (newFeed[index].is_default = 0);
                }
                /* eslint-disable max-len */
                try {
                    if (!topTabSelectedId) {
                        // 
                        let distributionIndex = 1;
                        if (newFeed[0]
                            && newFeed[0].component
                            && newFeed[0].component[distributionIndex]
                            && newFeed[0].component[distributionIndex].key === 'domesticDistribution') {
                            newFeed[0].component[distributionIndex].reportTableHasShow = this.data.get('reportTableHasShow');
                        }
                    }
                } catch (e) {
                    // error
                }
                /* eslint-enable max-len */
                this.data.set('feed', newFeed);
                this.nextTick(() => {
                    let height = document.getElementById('appContain').scrollHeight;
                    window.ReactNativeWebView && window.ReactNativeWebView.postMessage(height - 1);
                });
            }
        }).catch(() => {
        });
    }

    initData() {
        return {
            IS_IOS: env.isIOS,
            styles,
            feed: [],
            theme: 'kangyi-light',
            timeInfo: '',
            logPageArr: ['domestic', 'local', 'overseas'],
            requestParams: {
            },
            reportTableHasShow: false
        };
    }
}
