var invoke = require('../../core/invoke');


return {
    download: function (obj) {
        var arr = obj.name.split('-');
        var module = arr[0];
        var action = arr[1] || '';
        action = [module, action].join('/');

        return invoke(action, obj.data, obj.abtest);
    },
    prefetch: function (obj) {
        /* istanbul ignore next */
        var action = [obj.name, 'prefetch'].join('/');
        /* istanbul ignore next */
        return invoke(action, obj.data, obj.abtest);
    },
    request: function (obj) {
        /* istanbul ignore next */
        return invoke('net/request', obj, obj.abtest);
    }
};