
var $ = require('@baidu/Boxjs/dist/utils/_bdboxWrapper')
var invokeApp = require('./invokeApp');

/**
 * @function command
 * @memberOf Bdbox.android
 * @param  {Number} mode   0代表调起框界面；1代表调起第三方界面
 * @param  {String} intent 包含发送给客户端的参数
 * @param  {String} comClass  调起客户端具体activity，缺省
 * @param  {String} minV   调起的最小版本，可缺省
 * @return {Object}         返回对象格式的json
 */
function command(mode, intent, comClass, minV ) {
    var commParams = {
        "mode" : mode,
        "intent" : intent
    };

    if(typeof comClass != 'undefined'){
        commParams['class'] = comClass;
    }
    if(typeof minV != 'undefined'){
        commParams.min_v = minV;
    }

    var result = invokeApp('Bdbox_android_utils', 'command', [JSON.stringify(commParams)]);

    return result;
}

return command;
