
var $ = require('@baidu/Boxjs/dist/utils/_bdboxWrapper')
/* eslint-disable */
/**
 * 设置/修改O2O框架页面子标题
 * @memberOf Bdbox.client
 * @name setSubtitle
 * @author pengkuan
 */

var getVersion = require('../utils/getVersion'),
  versionCompare = require('../utils/version_compare'),
  iosInvoke = require('../ios/invokeApp'),
  androidInvoke = require('../android/invokeApp')

var self = function (title) {
  if (versionCompare(getVersion(), '6.2') < 0) {
    return
  }

  var params = {
    subtitle: title
  }

  params = JSON.stringify(params)

  if ($.$isIOS()) {
    iosInvoke('apppage', {
      action: 'changePage',
      minver: '6.2.0.0',
      params: params
    }, '')
  } else {
    androidInvoke('Bdbox_android_utils', 'changePage', [params, '{}'])
  }
}

return self
