
var $ = require('@baidu/Boxjs/dist/utils/_bdboxWrapper')
/* eslint-disable */

/**
 * 使用客户端弹toast；
 * @memberOf Bdbox.client
 * @name toast
 *
 */
var getVersion = require('../utils/getVersion');
var version_compare = require('../utils/version_compare');
var A_NAMESPACE = 'Bdbox_android_utils';
var ios_invokeApp = require('../ios/invokeApp');
var android_invokeApp = require('../android/invokeApp');

var self = function (string) {
    if ($.$isIOS()) {
        if ($.$isBox() && version_compare(getVersion(), '6.0') < 0) {
            return;
        }

        ios_invokeApp('utils', {
            action: 'toast',
            string: string,
            minver: '6.0.0.0'
        });

    } else {
        if ($.$isBox() && version_compare(getVersion(), '4.6') < 0) {
            return;
        }

        android_invokeApp(A_NAMESPACE, 'toast', [string]);
    }
};

return self;
