
var $ = require('@baidu/Boxjs/dist/utils/_bdboxWrapper')
/**
 * 给页面添加CSS样式
 * @param  {String} url [CSS链接或者是CSS样式]
 */
return function(url) {
    if (/.+\.css$/.test(url)) {
        var style = document.createElement('link');
        style.type = 'text/css';
        style.rel = 'stylesheet';
        style.href = url;
        (document.getElementsByTagName('head')[0] || document.getElementsByTagName('body')[0]).appendChild(style);
    } else {
        var style = document.createElement("style");
        style.type = 'text/css';
        (document.getElementsByTagName('head')[0] || document.getElementsByTagName('body')[0]).appendChild(style);
        style.innerHTML = url;
    }
}
