import {Component} from 'san';
import styles from './style.module.less';
import {aucArrowType} from './types';

export class aucArrow extends Component {
    static template:string = `
    <div class="{{wrapClass}}" on-click="handleClick"></div>
    `;
    static components:object = {
    };

    initData(): aucArrowType {
        return {
            styles,
            arrowType: ''
        };
    }
    static computed = {
        wrapClass() {
            return [
                styles.aucArrow,
                styles[this.data.get('arrowType')],
                this.data.get('landType') ? styles.bigEvent : ''
            ];
        }
    };

    attached(): void {
    }

    handleClick(): void {
        this.fire('click');
    }
}

export default aucArrow;
