import {Component} from 'san';
import styles from './style.module.less';
import {AucBackBarData} from './types';

export class AucBackBar extends Component {
    static template:string = /* html */ `
    <div class="{{styles.aucBackBarWrapper}}">
        <div className={{styles.infoContainer}}>
            <img className={{styles.infoIcon}} src={{backImg}} />
        </div>
        <div className={{styles.btnContainer}} on-click="buttonClick">{{text}}</div>
    </div>
    `;
    static components:object = {
    };

    initData(): AucBackBarData {
        return {
            styles,
            backImg: 'https://b.bdstatic.com/searchbox/icms/searchbox/img/baidu_slogan@2x.png',
            text: 'APP内阅读'
        };
    }

    attached(): void {
    }
    buttonClick() {
        this.fire('backShoubai');
    }
}

export default AucBackBar;
