import {Component} from 'san';

import initScrollView from './scrollView';

class AucComponent extends Component {
    constructor(props) {
        super(props);
    }
    scrollView:any;
    registerScrollInView():Promise<void> {
        return new Promise(resolve => {
            this.scrollView = initScrollView(resolve);
            this.scrollView.register(this.el);
        });
    }
}

export default AucComponent;
