import {env} from '@baidu/auc-base/env';
import BaseLog from '@baidu/feed-baselog';

const baseParams = {
    ct: 3,
    logFrom: 'feed_midbox',
    logInfo: 'a2_l_event_topic'
};

// thunder分发日志
export const topicVideoThunderLog = (type, params) => {
    if (!env.isSSR) {
        const baselog = new BaseLog({
            mode: 1,
            tParams: baseParams
        });
        baselog.sendLog({
            // 点击 cst 2 ，展现 cst 1
            cst: type === 'click' ? 2 : 1,
            ...params
        });
    }
};