export interface AucColumnData {
    styles?: object;
    LoaderState?: object;
    showTitle?: boolean; // 是否显示标题

    columnName: string; // 栏目名字
    columnType: number|string; // 栏目类型
    newsList: Array<object>; // 新闻列表
    isFold: boolean; // 首页展示3条还是5条
    loadingState: number; // loading状态
    isEnd: boolean; // 是否还有data
    columnIndex: number; // 栏目序号
    isLast: boolean; // 是否是最后一条
    columnExt?: object; // 事件脉络新增字段
    columnTags?: Array<string>; // 脉络标签tags
    currentIndex?:number; // 脉络tag标签选中当前索引值
    columnNameType: number; // 是否是定制化标题
    columnNameImg: object; // 定制化标题合集 
}
