/**
 * @file 专题头图标题组件
 * @author xiewenbo01 <xiewenbo01@baidu.com>
 */
import './index.less';
import {Component} from 'san';

const prefixCls = 'w-topic-header-title';
export default class HeaderTitle extends Component {
    static template = /* html */ `
        <div class="${prefixCls} {{typeClass}}">
            {{title}}
        </div>
    `;

    static trimWhitespace = 'all';

    static computed = {
        typeClass() {
            const type = this.data.get('type');
            if (type === 1 || type === 0 || (this.data.get('isThirdEdition') && type === 2)) {
                return 'type1';
            }
            return 'type2';
        },
    };

    initData() {
        return {
            title: '',
            type: 1,
        };
    }
}
