import {Component} from 'san';
import styles from './style.module.less';
import {AucMenuIconData} from './types';
import {WujiIcon} from '@baidu/wuji-san';
import '@baidu/wuji-icons/svg/d20-titlebar-more-o-66.svg';

export class AucMenuIcon extends Component {
    static template:string = /* html */ `
    <div class="{{styles.menuIcon}} {{isImmersive ? styles.isImmersive : ''}}" style="top: {{top}};" on-click="clickSet">
        <wuji-icon type="d20-titlebar-more-o-66" size="66"></wuji-icon>
    </div>
    `;
    static components:object = {
        'wuji-icon': WujiIcon
    };

    initData(): AucMenuIconData {
        return {
            styles,
            top: '0'
        };
    }

    attached(): void {
    }

    clickSet() {
        this.fire('click-set', '');
    }
}

export default AucMenuIcon;
