
import {AucHeatedDiscussionData} from '@baidu/auc-heated-discussion/types';
import {getBaseImageProps, transJumpProps} from '@baidu/auc-base/dataTransition';
import {getBaseComponentLogExtra} from './componentLogExtra';

export default (
    newsData:any,
    columnType:string,
    columnIndex:number
):AucHeatedDiscussionData => {
    const data:any = newsData.data;
    const likeType:string = data.liketype || '';
    const avatarSrc:string = data.auimage || '';
    let isUgc:boolean = likeType.indexOf('ugc') >= 0;
    let authorName:string = likeType.indexOf('ugc') >= 0 ? data.auname : data.source;

    return {
        isUgc,
        authorName,
        avatarSrc,
        description: data.description,
        imageProps: getBaseImageProps({src: data.image}),
        time: data.time,
        title: data.title,
        jumpProps: transJumpProps(newsData),
        logExtraParams: getBaseComponentLogExtra(newsData, columnType, columnIndex)
    };
};
