import {AucOperationPositionData} from '@baidu/auc-operation-position/types';

const lineToHump = (data) => {
    data.map((item) => {
        item.divIcon = item.div_icon;
        item.divWordColor = item.div_word_color;
        item.divTitle = item.div_title;
    })
    return data;
}

export default (operateData):AucOperationPositionData => {
    return {
        divType: operateData.div_type,
        divLine: operateData.div_line,
        divData: operateData.div_data ? lineToHump(operateData.div_data) : undefined,
        divBackImgOne: operateData.div_back_img_one,
        divBackImgTwo: operateData.div_back_img_double,
        divBackColor: operateData.div_back_color
    };
};