
import {AucTimelineData, AucTimelineItem} from '@baidu/auc-timeline/types';
import {transJumpProps, getBaseImageProps, transitionLabel} from '@baidu/auc-base/dataTransition';
import {getBaseComponentLogExtra} from './componentLogExtra';

export default (newsData, columnType, columnIndex):AucTimelineData => {
    return {
        time: newsData.time,
        timelineList: newsData.list.map((item):AucTimelineItem => {
            const data = item.data;
            return {
                description: data.description,
                imageProps: getBaseImageProps({src: item.layout === 'image1' && data.image || ''}),
                labelProps: transitionLabel(item),
                title: data.title,
                jumpProps: transJumpProps(item),
                logExtraParams: getBaseComponentLogExtra(item, columnType, columnIndex, 'timeline'),
                time: newsData.time
            }
        })
    };
};
