import AucComponent from '@baidu/auc-base/AucComponent';
import styles from './style.module.less';
import {AucZhengMingData} from './types';
import {WujiImage, WujiIcon} from '@baidu/wuji-san';
import  AucViewComp from './conponents/viewComp';
import {componentLog} from '@baidu/auc-log';



export class AucZhengMing extends AucComponent {
    static template:string = /* html */ `
        <div  class="{{styles.zhenWrap}}">
            <div class="{{styles.rightSide}}">
                <auc-views-point
                    s-for="item in rightSide"
                    s-bind="item.pointData"
                    on-back-shoubai="backShoubai"
                    on-log="log"
                />
            </div>
            <div class="{{styles.oppositeDide}}">
                <div class="{{styles.vsIconBg}}"></div>
                <span class="{{styles.vsIcon}}"></span>
                <auc-views-point
                    s-for="item in oppositeSide"
                    s-bind="item.pointData"
                    on-back-shoubai="backShoubai"
                    on-log="log"
                />
            </div>
        </div>
    `;

    static components:object = {
        'wuji-image': WujiImage,
        'wuji-icon': WujiIcon,
        'auc-views-point': AucViewComp
    };

    initData(): AucZhengMingData {
        return {
            styles
        };
    }

    attached(): void {
        this.registerScrollInView().then(_ => {
            this.log('zhengming_show');
        });
    }
    backShoubai(): void {
        this.fire('backShoubai');
    }
    log(type):void {
        componentLog(type, '', this.data.get('logExtraParams'));
    }
}

export default AucZhengMing;


