/**
 * @file 把 camel case 字符串转换成 kebab case
 * @author wangyongqing <wangyongqing01@baidu.com>
 */
/**
 * 把 camel case 字符串转换成 kebab case
 *
 * @param source 源字符串
 * @return 返回处理后的字符串
 * @example camel2kebab('abcFeDE'); // abc-fe-d-e
 */
export function camel2kebab(source) {
    return String(source).replace(/[A-Z]/g, match => '-' + match.toLowerCase());
}
