/**
 * 判断当前环境是否 Node 服务端
 *
 * @return 判断结果
 */
export function isSSR() {
    return typeof globalThis !== 'undefined'
        && typeof globalThis.window?.document?.createElement === 'undefined';
}

/**
 * 判断当前环境是否浏览器端
 *
 * @return 判断结果
 */
export function isBrowser() {
    return !isSSR();
}
