/**
 * @file 判断方法
 * @module is
 * @author wangyongqing <wangyongqing01@baidu.com>
 */
export interface KeyValue {
    [key: string]: unknown;
}
/**
 * 判断是否为 null 或者 undefined
 *
 * @param obj 判断的对象
 * @return 返回判断结果
 * @example
 * isNil(null) // true
 * isNil(undefined) // true
 * isNil('') // false
 */
export declare function isNil(obj: unknown): boolean;
/**
 * 判断是否为 undefined
 *
 * @param obj 判断的对象
 * @return 返回判断结果
 * @example
 * isUndefined(null) // false
 * isUndefined(undefined) // true
 * isUndefined('') // false
 */
export declare function isUndefined(obj: unknown): boolean;
/**
 * 判断是否是 window 对象
 *
 * @param obj 判断的对象
 * @return 返回判断结果
 * @example
 * isWindow(window) // true
 * isWindow(self) // true
 * isWindow('') // false
 */
export declare function isWindow(obj: unknown): boolean;
/**
 * 判断是否为普通对象
 *
 * @param obj 需要判断的对象
 * @return 返回判断结果
 * @example
 * isPlainObject({}) // true
 * isPlainObject(window) // false
 */
export declare function isPlainObject(obj: unknown): boolean;
/**
 * 是否是 function 类型
 *
 * @param obj 要判断的对象
 * @return 返回判断结果
 * @example
 * isFunction(new Function()) // true
 * isFunction('') // false
 */
export declare function isFunction(obj: unknown): boolean;
/**
 * 是否是 string 类型
 *
 * @param obj 要判断的对象
 * @return 返回判断结果
 * @example
 * isString([]) // false
 * isString('array') // true
 */
export declare function isString(obj: unknown): boolean;
/**
 * 是否是 array 类型
 *
 * @param obj 要判断的对象
 * @return 返回判断结果
 * @example
 * isArray([]) // true
 * isArray('array') // false
 */
export declare function isArray(obj: unknown): boolean;
/**
 * 是否是一个 Number 类型
 *
 * @param obj 要判断的对象
 * @return 返回判断结果
 * @example
 * isNumber(1) // true
 * isNumber('1') // false
 */
export declare function isNumber(obj: unknown): boolean;
/**
 * 判断是否为布尔类型
 *
 * @param bool 要判断的对象
 * @return 返回判断结果
 * @example
 * isBoolean(false) // true
 * isBoolean(/a/) // false
 */
export declare function isBoolean(bool: unknown): boolean;
/**
 * 判断参数是否是空字符串
 *
 * @param subject 被判断的字符串
 * @return 返回判断结果
 * @example
 * isEmpty(''); // => true
 * isEmpty('  '); // => false
 * isEmpty('World'); // => false
 */
export declare function isEmpty(subject: string): boolean;
/**
 * 判断参数是否是空格或者由空格组成
 *
 * @param subject 被判断的字符串
 * @return 判断结果
 * @example
 * isBlank('');// => true
 * isBlank('  ');// => true
 * isBlank('World');// => false
 */
export declare function isBlank(subject: string): boolean;
/**
 * 判断一个对象是不是一个空对象
 *
 * @param obj 待判断的对象
 * @return 判断结果
 * @example
 * isEmptyObject({}) // true
 */
export declare function isEmptyObject(obj: KeyValue): boolean;
/**
 * 判断一个对象是不是 JSONString
 *
 * @param obj 待判断的对象
 * @return 判断结果
 * @example
 * isJSON({}) // true
 */
export declare function isJSON(value: unknown): boolean;
