/**
 * 将传入的对象转换成数组
 *
 * @param arrayLike 被转对象
 * @return 返回array
 */
export declare function toArray(arrayLike: ArrayLike<unknown>): never[];
/**
 * 将传入的对象转换成数字
 *
 * @param value 被转对象
 * @param [defaultValue=null] 如果不能转，默认返回值
 * @return 返回 number
 */
export declare function toNumber(value: unknown, defaultValue?: unknown): unknown;
/**
 * 将传入的对象返回布尔
 *
 * @param value 被转对象
 * @param [defaultValue=false] 如果不能转，默认返回值
 * @return 返回boolean
 */
export declare function toBoolean(value: unknown, defaultValue?: unknown): unknown;
/**
 * 将传入对象返回字符串
 *
 * @function toString
 * @param value 被转对象
 * @param [defaultValue=null] 如果不能转，默认返回值
 * @return 返回字符串
 */
export declare function toString(value: unknown, defaultValue?: unknown): unknown;
