import { OpinionItem } from '@baidu/hd-opinion';
import { HOVER_STATUS } from './enum';
/**
 * action: 赞点击事件
 */
export declare const CLICK_PRAISE = "click:praise";
/**
 * payload: 点赞按钮当前状态
 */
export interface CLICK_PRAISE_PAYLOAD {
    /**
     * 点赞按钮当前状态
     */
    selected: boolean;
    /**
     * id
     */
    id: string;
    /**
     * 点赞数量
     */
    count: string | number;
}
/**
 * action: 发布器表情点击事件
 */
export declare const CLICK_PUBLISHER_EMOJI = "click:publisher:emoji";
/**
 * action: 发布器发表按钮点击事件
 */
export declare const CLICK_PUBLISHER_PUBLISH = "click:publisher:publish";
/**
 * payload: 发布器发表按钮点击事件参数
 */
export interface CLICK_PUBLISHER_PUBLISH_PAYLOAD {
    /**
     * 发布器文本框输入内容
     */
    content: string;
}
/**
 * action: 触发toast
 */
export declare const INVOKE_TOAST = "invoke:toast";
/**
 * action: toast消息参数
 */
export interface TOAST_PAYLOAD {
    type?: string;
    msg: string;
}
/**
 * action: 表情选择器单个表情点击事件
 */
export declare const CLICK_EMOJIPICKER_ITEM = "click:emojiPicker:item";
/**
 * payload: 表情选择器单个表情点击事件
 */
export interface CLICK_EMOJIPICKER_ITEM_PAYLOAD {
    /**
     * 表情文本
     */
    text: string;
    /**
     * 表情cdn资源url
     */
    url?: string;
}
/**
 * action: 单条弹幕的鼠标hover事件
 */
export declare const HOVER_DANMAKU_ITEM = "hover:danmaku:item";
/**
 * action: 单条弹幕的鼠标hover payload
 */
export interface HOVER_DANMAKU_ITEM_PAYLOAD {
    type: HOVER_STATUS;
}
/**
 * action: 单条表态点击事件
 */
export declare const CLICK_OPINION_ITEM = "click:opinion:item";
/**
 * action: 单条弹幕的鼠标hover payload
 */
export interface CLICK_OPINION_ITEM_PAYLOAD {
    index: number;
    list: OpinionItem[];
}
