/**
 * @description 获取数组中多个不重复的随机数
 * @param {Array} arr 数组
 * @param {Number} num 随机数个数
 * @returns {Array} 随机数组
 */
export function getRandoms(arr, num) {
    if (!Array.isArray(arr)) {
        throw new TypeError('参数不是数组类型');
    }
    const result = [];
    const length = arr.length;
    for (let i = 0; i < num; i++) {
        const randomIndex = Math.floor(Math.random() * (length - i - 1));
        const cur = arr[randomIndex];
        result.push(cur);
        /* eslint-disable no-param-reassign */
        [
            arr[randomIndex],
            arr[length - 1 - i],
        ] = [
            arr[length - 1 - i],
            arr[randomIndex],
        ];
        /* eslint-enable no-param-reassign */
    }
    return result;
}
export function encodeHtml(source) {
    source = String(source).replace(/</g, '&lt;').replace(/>/g, '&gt;');
    return source;
}
