export function invokeByIframe(schema) {
    let node = document.createElement('iframe'); // tslint:disable-line
    node.style.display = 'none';
    node.src = schema;
    const body = document.body;
    body.appendChild(node);
    // 销毁 iframe
    setTimeout(function () {
        body.removeChild(node);
        node = null;
    }, 0);
}