export type RecognizedBrand =
    'samsung'
    | 'xiaomi'
    | 'oppo'
    | 'vivo'
    | 'huawei'
    | 'honor'
    | 'apple';
// 正则匹配不到的，给个默认值 '-'
export type Brand = RecognizedBrand | '-';


export const brandMap: Record<RecognizedBrand, RegExp> = {
    samsung: /.*Android.*(SAMSUNG|SM-).*/i,
    huawei: /.*huawei.*/i,
    honor: /.*honor.*/i,
    oppo: /.*(OPPO|heytap|p[a-z][a-z][a-z]\d0).*/i,
    vivo: /.*Android.*(vivo|v\d\d\d\d[a-z]?a?).*/i,
    xiaomi: /.*Android.*(Mi|M2011K2C).*/i,
    apple: /(iPhone|iPod|iPad|Macintosh)/i,
};
