import {Browser} from '../app/browserMap';
import {AppName} from '../app/appConfigMap';

export type Os = 'ios' | 'android' | '*';
export type NotTryJumpRule = Array<[Browser | '*', AppName | '*', Os]>;

// 这里的匹配规则一定要从头到尾
// 存在优先级的
// [browser, appName, os]
// 从当前browser调起至目标appName，不尝试调起，直接走失败路径
export const notTryJumpRules: NotTryJumpRule = [
    ['xiaomi', 'baiduboxapp', 'android'],
    ['uc', 'baiduboxapp', 'android'],
    ['oppo', 'baiduboxapp', 'android'],
    ['vivo', 'baiduboxapp', 'android'],
    ['wechat', 'baiduboxapp', 'android'],
];
