/**
 * @file 检查是否处于黑名单浏览器中
 * @author zhuzhibo <zhuzhibo@baidu.com>
 */
import {notTryJumpRules} from '../../config/rules/notTryJumpRules';
import {Browser} from '../../config/app/browserMap';
import {AppName} from '../../config/app/appConfigMap';
import {Os} from '../detect/getHostInfo';

/**
 * 检查是否处于黑名单浏览器中
 */
export function checkNotTryJump(browser: Browser, appName: AppName, os: Os) {

    for (const [browserRule, appNameRule, osRule] of notTryJumpRules) {
        if (
            (browserRule === '*' || browserRule === browser)
            && (appNameRule === '*' || appNameRule === appName)
            && (osRule === '*' || osRule === os)
        ) {
            return true;
        }
    }
    return false;
}
