/**
 * hitMcp
 *
 * @param {Array} posVal 调起位的数组结果 有可能是个undefined
 * @param {number} index 某个物料对象，默认是第一个
 * @returns {boolean} 是否需要执行调起策略
 */
export function hitMcp(posVal, index = 0) {
    return posVal?.[index]?.action_flag !== undefined;
}
