import {processExistNodes, pollingRequest, observeAddedNodes} from './observe';
import {handleClick} from './handleClick';

/**
 * 监听dom元素
 *
 * @param {HTMLElement} targetNode 要监听的祖先dom元素
 */
export function observeDom(targetNode) {
    processExistNodes(targetNode);
    observeAddedNodes(targetNode);
    pollingRequest();
    handleClick(targetNode);
}
