// 模拟setTInterval

export function simulateInterval(callback, interval) {
    let timerId = null;

    function fn() {
        callback();
        const prevTimmerId = timerId;
        timerId = setTimeout(fn, interval);
        clearTimeout(prevTimmerId);
    }

    timerId = setTimeout(fn, interval);

    return {
        stop() {
            clearTimeout(timerId);
        }
    };
}

