import { dateFormat } from '@baidu/xbox';
/**
 * 用于计算头部时间（当为之前年份时候现实年月日，当为本年时，显示月-日 时:分）
 * @param {number|string} timestamp 时戳
 */
export const getHeadDate = (timestamp) => {
    timestamp = +timestamp;
    if (isNaN(timestamp)) {
        return '';
    }
    const now = new Date();
    const tar = new Date(timestamp);
    return now.getFullYear() !== tar.getFullYear() ? dateFormat(tar, 'yyyy-MM-dd') : dateFormat(tar, 'MM-dd hh:mm');
};
/**
 * 用于返回头部时间（统一显示年-月-日 时:分）
 * @param {number|string} timestamp 时戳
 */
export const getYearDate = (timestamp) => {
    timestamp = +timestamp;
    if (isNaN(timestamp)) {
        return '';
    }
    const tar = new Date(timestamp);
    return dateFormat(tar, 'yyyy-MM-dd hh:mm');
};
