/**
 * @file A stricter URI encode adhering to RFC 3986
 * @author wangyongqing <wangyongqing01@baidu.com>
 * https://github.com/kevva/strict-uri-encode/blob/master/index.js
 */

const oEncodeURIComponent = encodeURIComponent;

export function uriEncode(str: string) {
    return oEncodeURIComponent(str).replace(/[!'()*]/g, x => `%${x.charCodeAt(0).toString(16).toUpperCase()}`);
}
