declare type LoadFileCallback = (err: any, path: string) => void;
interface LoadFileParams {
    async?: boolean;
    maxTries?: number;
    timeout?: number;
    before?: (path: string, elem: Element) => unknown;
}
/**
 * 加载单个文件，支持 css/img/js
 *
 * @param path url 地址
 * @param callback 回调函数
 * @param params 配置参数，包括 async，timeout，maxTries，before 等
 * @param numTries 不对外参数，用于重试次数
 * @example
 * loadFile('/path/to/foo.js', function(err, path) {
 *     // foo.js loaded
 * });
 * loadFile('css!/path/to/foo', function(err, path) {
 *     // foo css loaded
 * });
 * loadFile('img!/path/to/foo', function(err, path) {
 *     // foo img loaded
 * });
 */
export declare function loadFile(path: string, callback: LoadFileCallback, params: LoadFileParams, numTries?: number): void;
export {};
