import type { KeyValue } from './is';
declare type IterableObject = KeyValue | ArrayLike<unknown>;
declare type IteratorMethod = (val: unknown, key: string | number, context?: IterableObject) => false | void;
/**
 * 对象遍历方法
 *
 * @param obj 要遍历的对象
 * @param iterator 处理函数
 * @param context 选填上下文
 * @example
 * each([1,2,3], (v, i)=>{console.log(v,i)});
 * 1,0
 * 2,1
 * 3,2
 * 注意：与 jQuery 和 zepto 的 each 不同，第一个参数是索引
 */
export declare function each(obj: IterableObject, iterator: IteratorMethod, context?: IterableObject): void;
export {};
