import { UA } from './ua';
import { isBox } from './is-box';
import { isMatrix } from './is-matrix';
import { isArray, isString } from './is';
/**
 * 判断是否是在某些 box 标识范围内
 *
 * @param subnames lite/info组合，array 或逗号','隔开字符串
 * @param ua userAgent
 * @return 判断结果
 */
export function isMaybeBox(subnames, ua = UA) {
    const list = isArray(subnames) ? subnames : isString(subnames) ? subnames.split(',') : [];
    let len = list.length;
    while (len--) {
        if (isBox(list[len], ua) || isMatrix(list[len], ua)) {
            return true;
        }
    }
    return false;
}
