let doc = null;
let parentNode = null;
/**
 * 给页面添加CSS样式
 *
 * @param url CSS链接或者是CSS样式
 */
export function addStyle(url) {
    if (!doc) {
        doc = document;
        parentNode = doc.head || doc.body || doc.getElementsByTagName('script')[0] || {};
    }
    if (/.+\.css$/.test(url)) {
        const link = doc.createElement('link');
        link.type = 'text/css';
        link.rel = 'stylesheet';
        link.href = url;
        parentNode === null || parentNode === void 0 ? void 0 : parentNode.appendChild(link);
    }
    else {
        const style = doc.createElement('style');
        style.type = 'text/css';
        // style.innerHTML = url; // innerHTML 触发安全检查问题
        style.innerText = url;
        parentNode === null || parentNode === void 0 ? void 0 : parentNode.appendChild(style);
    }
}
