import { guid } from './guid';
import { typeError } from './type-error';
import { globalObject } from './get-global-object';
/**
 * 生成全局函数名称
 *
 * @param func 需要挂载到全局的函数
 * @return 返回全局函数名称
 */
export function getGlobalFunc(func) {
    if (typeof func === 'function') {
        const name = `_xfunc_${guid()}`;
        globalObject[name] = (...args) => func.apply(globalObject, args);
        return name;
    }
    throw typeError('func', 'function', func);
}
