import Base from '@baidu/mo-base';
import { WujiImage, WujiTitle, } from '@baidu/wuji-san';
import styles from './style.module.less';
export * from './interface';
import { MIAODONG_CLICK } from './interface';
// @san/component
export default class TopicMiaodong extends Base {
    static template = /* html */ `
        <div
            class="{{styles.miaodongWrapper}}"
            on-click="handleClick"
        >
            <wuji-title
                text="{{title}}"
                spacing="{{false}}"
                visited="{{visited}}"
                class="{{styles.title}}"
            />
            <wuji-image
                s-bind="{{imageProps}}"
            />
        </div>
    `;
    static components = {
        'wuji-image': WujiImage,
        'wuji-title': WujiTitle,
    };
    static computed = {};
    initData() {
        return {
            styles,
            visited: false,
        };
    }
    attached() {
        super.attached();
    }
    handleClick(e) {
        e.stopPropagation();
        this.data.set('visited', true);
        this.eventDispatch(MIAODONG_CLICK);
    }
}
