/**
 * @file 组件 flash 熊掌扫光动效
 * @author baozhixin <baozhixin@baidu.com>
 *
 * 布局layout：
 * 1、auto: 默认撑满flex布局的容器(依赖父容器样式)
 * 2、full: fixed布局占满全屏
 */

import './style/index.less';
import {Component, DataTypes} from 'san';

const prefixCls = 's-flash';

export default class Flash extends Component {
    // eslint-disable-next-line
    static template = /*html*/ `
        <div class="{{className}}">
            <div class="${prefixCls}-logo">
                <div class="${prefixCls}-flash"/>
                <div class="${prefixCls}-mask"/>
            </div>
        </div>
    `;

    static dataTypes = {
        layout: DataTypes.oneOf(['auto', 'full'])
    };

    static computed = {
        className() {
            const isFull = this.data.get('layout') === 'full';
            return [
                prefixCls,
                isFull && `${prefixCls}-fullpage`
            ].filter(c => c);
        }
    };

    initData() {
        return {
            layout: 'auto'
        };
    }
}
