/**
 * @file 组件 sidebar 侧边栏
 * @author xueruijun <xueruijun@baidu.com>
 */

import './style/index.less';
import {Component, DataTypes} from 'san';

const prefixCls = 's-sidebar';

export default class Sidebar extends Component {
    // eslint-disable-next-line
    static template = /*html*/ `
        <div class="${prefixCls}">
            <span
                s-for="item in list"
                class="name {{item === activeItem ? 'selected' : ''}}"
                on-click="switch(item)"
            >{{item}}</span>
        </div>
    `;

    static dataTypes = {
        list: DataTypes.array,
        activeItem: DataTypes.string
    };

    switch(item) {
        this.fire('switch', item);
    }
}
