/**
 * 获取某cookie
 *
 * @param  {string} name 参数名
 * @return {string} 值
 */
export function getCookie(name: string): string {
    const reg = new RegExp('(^| )' + name + '=([^;]*)(;|$)');
    const arr = document.cookie.match(reg);
    return arr ? decodeURIComponent(arr[2]) : '';
}
