/**
 * @file 手机厂商market头规则
 * @author zhangsiyuan(zhangsiyuan@baidu.com)
 */
import {RecognizedBrowser} from '../app/browserMap';
import {RecognizedBrand} from '../app/brandMap';
import {MarketName} from '../app/marketMap';

// 这里的匹配规则一定要从头到尾
// 存在优先级的
// [browser, brand, marketScheme]
export const androidStoreRules: Array<[RecognizedBrowser | '*', RecognizedBrand | '*', MarketName]> = [
    // 三星不支持market协议,优先级最高
    ['*', 'samsung', 'samsungapps'],

    // 微博是白名单机制，所以优先级要高,除了三星
    ['weibo', '*', 'market'],
    // 手百是白名单机制，所以优先级要高
    ['baiduboxapp', '*', 'market'],
    ['baiduboxlite', '*', 'market'],
    ['tomas', '*', 'market'],

    // 小米 红米 uc 搜狗需要走mi 商店
    ['xiaomi', 'xiaomi', 'mimarket'],
    ['uc', 'xiaomi', 'mimarket'],
    ['sogou', 'xiaomi', 'mimarket'],

    // qq浏览器比较特殊，华为荣耀只能走market，不然多重调起容易没反应
    ['qqbrowser', 'huawei', 'market'],
    ['qqbrowser', 'honor', 'market'],
    // 华为 荣耀机型都走自己的应用商店
    ['*', 'huawei', 'appmarket'],
    ['*', 'honor', 'appmarket'],

    // oppo
    ['oppo', 'oppo', 'oppomarket'],
    ['uc', 'oppo', 'oppomarket'],
    ['sogou', 'oppo', 'oppomarket'],
    ['quark', 'oppo', 'oppomarket'],

    // vivo
    ['uc', 'vivo', 'vivomarket'],
    ['sogou', 'vivo', 'vivomarket'],

];
