export const isRangeIn = (num, maxnum, minnum) => {
    return parseFloat(num) >= minnum
        && parseFloat(num) <= maxnum;
};

export const changeStyle = (drag, oLeft, oTop) => {
    drag.style.cssText = `
        left: ${oLeft}px;
        top: ${oTop}px
    `;
};

