/**
 * @file is-server
 * @author baozhixin <baozhixin@baidu.com>
 *
 * 是否服务端环境
 */

let isServer;

export default () => {
    if (isServer === undefined) {
        if (typeof window === 'undefined' && typeof global !== 'undefined') {
            isServer = true;
        }
        else {
            isServer = false;
        }
    }
    return isServer;
};
