import {Component} from 'san';

const prefixCls = 'price';

export default class Price extends Component {
    static template = /* html */ `
        <div class="${prefixCls}">
            <div class="${prefixCls}-desc">{{desc}}</div>
            <div class="${prefixCls}-price">{{price}}</div>
            <div class="${prefixCls}-unit">{{unit}}</div>
        </div>
    `;
    initData() {
        return {
            desc: '',
            price: '',
            unit: '',
        };
    }
}
