import {Component} from 'san';
import WujiGrid from '../../wuji-grid';
import WujiButton from '../../wuji-button';
/*
 * type字段枚举值说明:
 * 0--单个按钮
 * 1--文字+按钮
 * 2--价格+按钮
 * 3--价格
 * 4--双按钮
 */

export default class TextButton extends Component {
    static template = /* html */ `
        <div class="text-button">
            <wuji-row column="24" class="gridContainer" gutter="24" margin="0" align="center">
                <wuji-col s-if="type !== 0">
                    <div s-if="type === 1" class="text">
                        <span class="text1">{{text1}}</span>
                        <span class="text2">{{text2}}</span>
                    </div>
                    <div s-if="type === 2 || type === 3" class="text">
                        <span class="text1-price">{{text1}}</span>
                        <span class="text2-unit">{{text2}}</span>
                    </div>
                    <wuji-button
                        s-if="type === 4"
                        theme="outline"
                        text="{{btnText1}}"
                        height="84"
                        color="{{btnColor1}}"
                    />
                </wuji-col>
                <wuji-col
                    s-if="type === 0 || type === 1 || type === 2 || type === 4"
                    span="{{span}}"
                >
                    <wuji-button theme="outline" text="{{btnText2}}" height="84" color="{{btnColor2}}"/>
                </wuji-col>
            </wuji-row>
        </div>
    `;
    static components = {
        'wuji-row': WujiGrid.Row,
        'wuji-col': WujiGrid.Col,
        'wuji-button': WujiButton,
    };
}
